
INSERT INTO temp_content (`bab`, `title 1`, `title 2`, `title 3`, `type`, `text`, `description`)
VALUES
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الأول : في التعقيبات العامّة', 'تکبیرات بعد از هر نماز', '', 1, 'فإذا سلّمت وفرغت من الصلاة فقل: الله أَكْبَرُ ـ ثلاث مرات ـ رافعاً عند كلّ تكبيرة يديك
إلى حيال اُذنيك ثمّ قل:', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الأول : في التعقيبات العامّة', 'دعای لَا إِلٰهَ إِلّا اللّٰهُ إِلٰهاً وَاحِداً ', '', 2, 'لَا إِلٰهَ إِلّا اللّٰهُ إِلٰهاً وَاحِداً وَنَحْنُ لَهُ مُسْلِمُونَ، لَاإِلٰهَ إِلّا اللّٰهُ وَ لَا نَعْبُدُ إِلّا إِيَّاهُ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الأول : في التعقيبات العامّة', '', '', 2, 'مُخْلِصِينَ لَهُ الدِّينَ وَلَوْ كَرِهَ الْمُشْرِكُونَ، لَاإِلٰهَ إِلّا اللّٰهُ رَبُّنَا وَ رَبُّ آبائِنَا الْأَوَّلِينَ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الأول : في التعقيبات العامّة', '', '', 2, 'اإِلٰهَ إِلّا اللّٰهُ وَحْدَهُ وَحْدَهُ وَحْدَهُ ، أَنْجَزَ وَعْدَهُ، وَنَصَرَ عَبْدَهُ، وَأَعَزَّ جُنْدَهُ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الأول : في التعقيبات العامّة', '', '', 2, 'وَهَزَمَ الْأَحْزَابَ وَحْدَهُ، فَلَهُ الْمُلْكُ، وَلَهُ الْحَمْدُ، يُحْيِى وَيُمِيتُ، وَيُمِيتُ وَيُحْيِى', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الأول : في التعقيبات العامّة', '', '', 2, 'وَهُوَ حَيٌّ لَايَمُوتُ، بِيَدِهِ الْخَيْرُ وَهُوَ عَلَىٰ كُلِّ شَىْءٍ قَدِيرٌ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الأول : في التعقيبات العامّة', 'استغفار بعد از هر نماز', '', 1, 'ثمّ قل:', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الأول : في التعقيبات العامّة', '', '', 2, 'أَسْتَغْفِرُ اللّٰهَ الَّذى لَا إِلٰهَ إِلّا هُوَ الْحَىُّ الْقَيُّوْمُ وَ أَتُوْبُ إِلَيْهِ.', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الأول : في التعقيبات العامّة', 'دعای اللّٰهُمَّ اهْدِنِى مِنْ عِنْدِكَ', '', 1, 'ثمّ قل:', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الأول : في التعقيبات العامّة', '', '', 2, 'اللّٰهُمَّ اهْدِنِى مِنْ عِنْدِكَ، وَأَفِضْ عَلَىَّ مِنْ فَضْلِكَ، وَانْشُرْ عَلَىَّ مِنْ رَحْمَتِكَ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الأول : في التعقيبات العامّة', '', '', 2, 'وَأَنْزِلْ عَلَىَّ مِنْ بَرَكاتِكَ، سُبْحانَكَ لَاإِلٰهَ إِلّا أَنْتَ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الأول : في التعقيبات العامّة', '', '', 2, 'اغْفِرْ لِى ذُنُوبِى كُلَّها جَمِيعاً، فَإِنَّهُ لا يَغْفِرُ الذُّنُوبَ كُلَّها جَمِيعاً إِلّا أَنْتَ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الأول : في التعقيبات العامّة', '', '', 2, 'اللّٰهُمَّ إِنِّى أَسْأَلُكَ مِنْ كُلِّ خَيْرٍ أَحَاطَ بِهِ عِلْمُكَ، وَأَعُوذُ بِكَ مِنْ كُلِّ شَرٍّ أَحَاطَ بِهِ عِلْمُكَ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الأول : في التعقيبات العامّة', '', '', 2, 'اللّٰهُمَّ إِنِّى أَسْأَلُكَ عَافِيَتَكَ فِى أُمُورِى كُلِّها، وَأَعُوذُ بِكَ مِنْ خِزْىِ الدُّنْيَا وَعَذَابِ الْآخِرَةِ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الأول : في التعقيبات العامّة', '', '', 2, 'وَأَعُوذُ بِوَجْهِكَ الكَرِيمِ، وَعِزَّتِكَ الَّتِى لَاتُرامُ، وَقُدْرَتِكَ الَّتِى لَايَمْتَنِعُ مِنْهَا شَىْءٌ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الأول : في التعقيبات العامّة', '', '', 2, 'مِنْ شَرِّ الدُّنْيا وَالْآخِرَةِ، وَمِنْ شَرِّ الْأَوْجاعِ كُلِّهَا، وَمِنْ شَرِّ كُلِّ دابَّةٍ أَنْتَ آخِذٌ بِنَاصِيَتِها', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الأول : في التعقيبات العامّة', '', '', 2, 'إِنَّ رَبِّى عَلىٰ صِراطٍ مُسْتَقِيمٍ، وَلَا حَوْلَ وَلَا قُوَّةَ إِلّا بِاللّٰهِ الْعَلِيِّ الْعَظِيمِ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الأول : في التعقيبات العامّة', '', '', 2, 'تَوَكَّلْتُ عَلَى الْحَيِّ الَّذِي لَايَمُوتُ، وَالْحَمْدُ لِلّٰهِ الَّذِى لَمْ يَتَّخِذْ وَلَداً', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الأول : في التعقيبات العامّة', '', '', 2, 'وَلَمْ يَكُنْ لَهُ شَرِيكٌ فِى الْمُلْكِ، وَلَمْ يَكُنْ لَهُ وَلِيٌّ مِنَ الذُّلِّ، وَكَبِّرْهُ تَكْبِيراً', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الأول : في التعقيبات العامّة', 'تسبيح الزهراء عليها‌السلام', '', 1, 'ثمّ سبِّح تسبيح الزهراء عليها‌السلام وقل عشر مرّات قبل أن تتحرّك من موضعك:', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الأول : في التعقيبات العامّة', 'تهلیل بعد از هر نماز وهنگام طلوع وغروب', '', 2, 'أَشْهَدُ أَنْ لَا إِلٰهَ إِلّا اللّٰهُ وَحْدَهُ لَاشَرِيكَ لَهُ إِلٰهاً وَاحِداً أَحَداً فَرْداً صَمَداً، لَمْ يَتَّخِذْ صاحِبَةً وَلا وَلَداً.', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الأول : في التعقيبات العامّة', '', '', 1, 'أقول: رُوي لهذا التهليل فضل كثير سيّما إذا عُقِّب به صلاة الصبح والعشاء، وإذا قرئ عند طلوع الشمس وغروبها.', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الأول : في التعقيبات العامّة', 'دعای سُبْحانَ اللّٰهِ ', '', 2, 'سُبْحانَ اللّٰهِ كُلَّما سَبَّحَ اللّٰهَ شَيْءٌ، وَكَمَا يُحِبُّ اللّٰهُ أَنْ يُسَبَّحَ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الأول : في التعقيبات العامّة', '', '', 2, 'وَكَمَا هُوَ أَهْلُهُ، وَكَمَا يَنْبَغِى لِكَرَمِ وَجْهِهِ وَعِزِّ جَلالِهِ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الأول : في التعقيبات العامّة', '', '', 2, 'وَالْحَمْدُ لِلّٰهِ كُلَّما حَمِدَ اللّٰهَ شَىْءٌ، وَكَمَا يُحِبُّ اللّٰهُ أَنْ يُحْمَدَ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الأول : في التعقيبات العامّة', '', '', 2, 'وَكَمَا هُوَ أَهْلُهُ، وَ كَمَا يَنْبَغِى لِكَرَمِ وَجْهِهِ وَعِزِّ جَلَالِ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الأول : في التعقيبات العامّة', '', '', 2, 'وَلَا إِلٰهَ إِلّا اللّٰهُ كُلَّما هَلَّلَ اللّٰهَ شَىْءٌ، وَكَمَا يُحِبُّ اللّٰهُ أَنْ يُهَلَّلَ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الأول : في التعقيبات العامّة', '', '', 2, 'وَكَمَا هُوَ أَهْلُهُ، وَكَمَا يَنْبَغِى لِكَرَمِ وَجْهِهِ وَعِزِّ جَلَالِهِ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الأول : في التعقيبات العامّة', '', '', 2, 'وَاللّٰهُ أَكْبَرُ كُلَّما كَبَّرَ اللّٰهَ شَىْءٌ، وَكَمَا يُحِبُّ اللّٰهُ أَنْ يُكَبَّ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الأول : في التعقيبات العامّة', '', '', 2, 'وَكَمَا هُوَ أَهْلُهُ، وَكَمَا يَنْبَغِى لِكَرَمِ وَجْهِهِ وَعِزِّ جَلالِهِ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الأول : في التعقيبات العامّة', '', '', 2, 'سُبْحانَ اللّٰهِ، وَالْحَمْدُ لِلّٰهِ، وَلَا إِلٰهَ إِلّا اللّٰهُ، وَاللّٰهُ أَكْبَرُ عَلَىٰ كُلِّ نِعمَةٍ أَنْعَمَ بِهَا عَلَىَّ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الأول : في التعقيبات العامّة', '', '', 2, 'وَعَلَىٰ كُلِّ أَحَدٍ مِنْ خَلْقِهِ مِمَّنْ كَانَ أَوْ يَكُونُ إِلَىٰ يَوْمِ الْقِيَامَةِ ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الأول : في التعقيبات العامّة', '', '', 2, 'اللّٰهُمَّ إِنِّى أَسْأَلُكَ أَنْ تُصَلِّىَ عَلَىٰ مُحَمَّدٍ وَآلِ مُحَمَّدٍ، وَأَسْأَلُكَ مِنْ خَيْرِ مَا أَرْجُو', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الأول : في التعقيبات العامّة', '', '', 2, 'وَ خَيْرِ مَا لَا أَرْجُو، وَأَعُوذُ بِكَ مِنْ شَرِّ مَا أَحْذَرُ وَمِنْ شَرِّ مَا لَاأَحْذَرُ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الأول : في التعقيبات العامّة', 'اعمال بعد از نماز', '', 1, 'ثمّ تقرأ سورة الحمد، وآية الكرسيّ، وآيه: (شَهِدَ اللَّهُ)، وآية: (قُلِ اللَّهُمَّ مَالِكَ الْمُلْكِ)، وآية السَّخرة وهي آيات ثلاث من سورة الأعراف أوّلها: (إِنَّ رَبَّكُمُ اللَّهُ)، وآخرها: (مِّنَ الْمُحْسِنِينَ).
ثمّ تقول ثلاثاً: ', 'لینک به آیات وصل شود'),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الأول : في التعقيبات العامّة', '', '', 2, 'سُبْحَانَ رَبِّكَ رَبِّ الْعِزَّةِ عَمَّا يَصِفُونَ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الأول : في التعقيبات العامّة', '', '', 2, 'وَسَلَامٌ عَلَى الْمُرْسَلِينَ ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الأول : في التعقيبات العامّة', '', '', 2, 'وَالْحَمْدُ لِلّٰهِ رَبِّ الْعالَمِينَ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الأول : في التعقيبات العامّة', 'دعای حضرت یوسف (ع) در زندان', '', 1, 'ثمّ تقول ثلاث مرّات: ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الأول : في التعقيبات العامّة', '', '', 2, 'اللّٰهُمَّ صَلِّ عَلَىٰ مُحَمَّدٍ وَآلِ مُحَمَّدٍ، وَاجْعَلْ لِى مِنْ أَمْرِىَ فَرَجاً وَمَخْرَجاً', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الأول : في التعقيبات العامّة', '', '', 2, 'وَارْزُقْنِى مِنْ حَيْثُ أَحْتَسِبُ وَمِنْ حَيْثُ لَاأَحْتَسِبُ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الأول : في التعقيبات العامّة', '', '', 1, 'وهذا دعاء علّمه جبرائيل يوسف عليه‌السلام في السجن.', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الأول : في التعقيبات العامّة', 'صلوات وتعجیل فرج بعد از هر نماز', '', 1, 'ثمّ خذ لحيتك بيدك اليمنى، وابسط يدك اليسرى إلى السماء، وقل سبع مرّات: ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الأول : في التعقيبات العامّة', '', '', 2, 'يَا رَبَّ مُحَمَّدٍ وَآلِ مُحَمَّدٍ، صَلِّ عَلىٰ مُحَمَّدٍ وَآلِ مُحَمَّدٍ، وَعَجِّلْ فَرَجَ آلِ مُحَمَّدٍ.', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الأول : في التعقيبات العامّة', 'دعای یا ذا الجلال والکرام', '', 1, 'وقل ثلاثاً وأنت علىٰ ذلك الحال:', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الأول : في التعقيبات العامّة', '', '', 2, 'يَا ذَاالْجَلالِ وَ الْإِكْرامِ، صَلِّ عَلىٰ مُحَمَّدٍ وَآلِ مُحَمَّدٍ، وَارْحَمْنِى وَ أَجِرْنِى مِنَ النَّارِ.', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الأول : في التعقيبات العامّة', 'تلاوت سوره اخلاص بعد از نماز', '', 1, 'ثمّ تقرأ اثنتي عشرة مرّة سورة: (قُلْ هُوَ اللَّهُ أَحَدٌ). وتقول: ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الأول : في التعقيبات العامّة', 'دعای اللّٰهُمَّ إِنِّى أَسْأَلُكَ بِاسْمِكَ الْمَكْنُونِ', '', 2, 'اللّٰهُمَّ إِنِّى أَسْأَلُكَ بِاسْمِكَ الْمَكْنُونِ الْمَخْزُونِ، الطَّاهِرِ الطُّهْرِ الْمُبَارَكِ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الأول : في التعقيبات العامّة', '', '', 2, 'وَأَسْأَلُكَ بِاسْمِكَ الْعَظِيمِ وَسُلْطَانِكَ الْقَدِيمِ، يَا وَاهِبَ الْعَطَايَا، وَيَا مُطْلِقَ الْأُسَارَىٰ، وَيَا فَكَّاكَ الرِّقابِ مِنَ النَّارِ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الأول : في التعقيبات العامّة', '', '', 2, 'أَسْأَلُكَ أَنْ تُصَلِّىَ عَلَىٰ مُحَمَّدٍ وَآلِ مُحَمَّدٍ وَأَنْ تُعْتِقَ رَقَبَتِى مِنَ النَّارِ ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الأول : في التعقيبات العامّة', '', '', 2, 'وَأَنْ تُخْرِجَنِى مِنَ الدُّنْيَا سَالِماً، وَتُدْخِلَنِى الْجَنَّةَ آمِناً،', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الأول : في التعقيبات العامّة', '', '', 2, 'وَأَنْ تَجْعَلَ دُعَائِى أَوَّلَهُ فَلَاحاً، وَ أَوْسَطَهُ نَجاحَاً', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الأول : في التعقيبات العامّة', '', '', 2, 'وَآخِرَهُ صَلَاحاً، إِنَّكَ أَ نْتَ عَلَّامُ الْغُيُوبِ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الأول : في التعقيبات العامّة', 'دعای صحیفه علویه بعد از هر نماز', '', 1, 'وورد في الصحيفة العلويّة لتعقيب الفرائض: ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الأول : في التعقيبات العامّة', '', '', 2, 'يَا مَنْ لَايَشْغَلُهُ سَمْعٌ عَنْ سَمْعٍ، وَ يَا مَنْ لَايُغَلِّطُهُ السَّائِلُونَ، وَ يَا مَنْ لَايُبْرِمُهُ إِلْحَاحُ الْمُلِحِّينَ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الأول : في التعقيبات العامّة', '', '', 2, 'أَذِقْنِى بَرْدَ عَفْوِكَ، وَ حَلَاوَةَ رَحْمَتِكَ وَمَغْفِرَتِكَ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الأول : في التعقيبات العامّة', '', '', 1, 'وتقول أيضاً: ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الأول : في التعقيبات العامّة', 'دعای إِلٰهِى هٰذِهِ صَلاتِى صَلَّيْتُها لَالِحاجَةٍ مِنْكَ إِلَيْهَا', '', 2, 'إِلٰهِى هٰذِهِ صَلاتِى صَلَّيْتُها لَالِحاجَةٍ مِنْكَ إِلَيْهَا، وَلَا رَغْبَةٍ مِنْكَ فِيهَا', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الأول : في التعقيبات العامّة', '', '', 2, 'إِلّا تَعْظِيماً وَطَاعَةً وَ إِجَابَةً لَكَ إِلَىٰ مَا أَمَرْتَنِى بِهِ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الأول : في التعقيبات العامّة', '', '', 2, 'إِلٰهِى إِنْ كَانَ فِيها خَلَلٌ أَوْ نَقْصٌ مِنْ رُكُوعِها أَوْ سُجُودِها فَلَا تُؤاخِذْنِى، وَ تَفَضَّلْ عَلَىَّ بِالْقَبُولِ وَالْغُفْرانِ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الأول : في التعقيبات العامّة', 'دعای حافظه بعد از هر نماز', '', 1, 'وتدعو أيضاً عقيب الصّلوات بهذا الدعاء الذي علّمه النبي صلى‌الله‌عليه‌وآله‌وسلم أمير المؤمنين عليه‌السلام للذّاكرة: ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الأول : في التعقيبات العامّة', '', '', 2, 'سُبْحَانَ مَنْ لَايَعْتَدِى عَلىٰ أَهْلِ مَمْلَكَتِهِ، سُبْحانَ مَنْ لَايَأْخُذُ أَهْلَ الْأَرْضِ بِأَلْوانِ الْعَذَابِ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الأول : في التعقيبات العامّة', '', '', 2, 'سُبْحانَ الرَّؤُوُفِ الرَّحِيمِ ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الأول : في التعقيبات العامّة', '', '', 2, 'اللّٰهُمَّ اجْعَلْ لِى فِى قَلْبِى نُوراً وَ بَصَرَاً وَ فَهْماً وَ عِلْماً، إِنَّكَ عَلىٰ كُلِّ شَىْءٍ قَدِيرٌ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الأول : في التعقيبات العامّة', 'دعای أُعِيذُ نَفْسِى وَ دِينِى وَأَهْلِى وَمَالِى', '', 1, 'وقال الكفعمي في المصباح: قل ثلاث مرّات عقيب الصلوات: ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الأول : في التعقيبات العامّة', '', '', 2, 'أُعِيذُ نَفْسِى وَ دِينِى وَأَهْلِى وَمَالِى وَ وَلَدِى وَ إِخْوانِى فِى دِينِى', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الأول : في التعقيبات العامّة', '', '', 2, 'وَمَا رَزَقَنِى رَبِّى، وَخَواتَِيمَ عَمَلِى، وَمَنْ يَعْنِينِى أَمْرُهُ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الأول : في التعقيبات العامّة', '', '', 2, 'بِاللّٰهِ الْوَاحِدِ الْأَحَدِ الصَّمَدِ الَّذِى لَمْ يَلِدْ وَلَمْ يُولَدْ وَلَمْ يَكُنْ لَهُ كُفُواً أَحَدٌ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الأول : في التعقيبات العامّة', '', '', 2, 'وَبِرَبِّ الْفَلَقِ مِنْ شَرِّ مَا خَلَقَ، وَمِنْ شرِّ غَاسِقٍ إِذَا وَقَبَ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الأول : في التعقيبات العامّة', '', '', 2, 'وَمِنْ شَرِّ النَّفَّاثَاتِ فى الْعُقَدِ، وَمِنْ شَرِّ حَاسِدٍ إِذَا حَسَدَ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الأول : في التعقيبات العامّة', '', '', 2, 'وَبِرَبِّ النَّاسِ، مَلِكِ النَّاسِ، إِلٰهِ النَّاسِ، مِنْ شَرِّ الْوَسْوَاسِ الخَنَّاسِ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الأول : في التعقيبات العامّة', '', '', 2, 'الَّذِى يُوَسْوِسُ فِى صُدُورِ النَّاسِ مِنَ الْجِنَّةِ وَالنَّاسِ.', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الأول : في التعقيبات العامّة', 'دعای اللّٰهُمَّ إِنَّ مَغْفِرَتَكَ أَرْجَىٰ مِنْ عَمَلِی', '', 1, 'وعن خطّ الشيخ الشهيد أنّ النبيّ صلى‌الله‌عليه‌وآله‌وسلم قال: مَن أراد أن لا يقفه الله يوم القيامة علىٰ قبيح
أعماله، ولا ينشر له ديوان فليقرء هذا الدعاء في دبر كلّ صلاة:', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الأول : في التعقيبات العامّة', '', '', 2, 'اللّٰهُمَّ إِنَّ مَغْفِرَتَكَ أَرْجَىٰ مِنْ عَمَلِى، وَ إِنَّ رَحْمَتَكَ أَوْسَعُ مِنْ ذَنْبِى', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الأول : في التعقيبات العامّة', '', '', 2, 'اللّٰهُمَّ إِنْ كَانَ ذَنْبِى عِنْدَكَ عَظِيماً، فعَفْوُكَ أَعْظَمُ مِنْ ذَنْبِى', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الأول : في التعقيبات العامّة', '', '', 2, 'اللّٰهُمَّ إِنْ لَمْ أَكُنْ أَهْلاً أَنْ أَبْلُغَ رَحْمَتَكَ، فَرَحْمَتُكَ أَهْلٌ أَنْ تَبْلُغَنِى وَتَسَعَنِى', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الأول : في التعقيبات العامّة', '', '', 2, 'لِأَنَّهَا وَسِعَتْ كُلَّ شَىْءٍ، بِرَحْمَتِكَ يَا أَرْحَمَ الرَّاحِمِينَ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الأول : في التعقيبات العامّة', 'دعای انت السلام ومنک السلام', '', 1, 'وعن ابن بابويه رحمه‌الله، قال: إذا فرغت من تسبيح فاطمة عليها‌السلام فقل:', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الأول : في التعقيبات العامّة', '', '', 2, 'اللّٰهُمَّ أَنْتَ السَّلَامُ، وَمِنْكَ السَّلَامُ، وَلَكَ السَّلَامُ، وَ إِلَيْكَ يَعُودُ السَّلَامُ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الأول : في التعقيبات العامّة', '', '', 2, 'سُبْحَانَ رَبِّكَ رَبِّ الْعِزَّةِ عَمَّا يَصِفُونَ، وَسَلَامٌ عَلَى الْمُرْسَلِينَ، وَالْحَمْدُ لِلّٰهِ رَبِّ الْعَالَمِينَ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الأول : في التعقيبات العامّة', '', '', 2, 'السَّلَامُ عَلَيْكَ أَيُّهَا النَّبِىُّ وَرَحْمَةُ اللّٰهِ وَبَرَكاتُهُ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الأول : في التعقيبات العامّة', '', '', 2, 'السَّلامُ عَلَى الْأَئِمَّةِ الْهَادِينَ الْمَهْدِيِّينَ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الأول : في التعقيبات العامّة', '', '', 2, 'السَّلَامُ عَلىٰ جَمِيعِ أَنْبِياءِ اللّٰهِ وَرُسُلِهِ وَمَلَائِكَتِهِ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الأول : في التعقيبات العامّة', '', '', 2, 'السَّلامُ عَلَيْنا وَعَلَىٰ عِبادِ اللّٰهِ الصَّالِحِينَ؛', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الأول : في التعقيبات العامّة', '', '', 1, 'وتدعو بما أحببت.', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الأول : في التعقيبات العامّة', 'دعای رضیت بالله ربا وبالاسلام دینا', '', 1, 'وقال الكفعمي تقول بعد الصلوات: ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الأول : في التعقيبات العامّة', '', '', 2, 'رَضِيتُ بِاللّٰهِ رَبّاً، وَبِالْإِسْلامِ دِيناً، وَبِمُحَمَّدٍ صَلَّى اللّٰهُ عَلَيْهِ وَآلِهِ نَبِيّاً', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الأول : في التعقيبات العامّة', '', '', 2, 'وَبِعَلِيٍّ إِمَاماً، وَ بِالْحَسَنِ وَ الْحُسَيْنِ وَ عَلِيٍّ وَ مُحَمَّدٍ وَجَعْفَرٍ وَ مُوسىٰ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الأول : في التعقيبات العامّة', '', '', 2, 'وَعَلِيٍّ وَمُحَمَّدٍ وَ عَلِيٍّ وَ الْحَسَنِ وَ الْخَلَفِ الصَّالِحِ عَلَيْهِمُ السَّلامُ، أَئِمَّةً وَسَادَةً وَقادَةً', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الأول : في التعقيبات العامّة', '', '', 2, 'بِهِمْ أَتَوَلَّىٰ، وَمِنْ أَعْدائِهِمْ أَتَبَرَّأُ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الأول : في التعقيبات العامّة', 'دعای اللّٰهُمَّ إِنِّى أَسْأَلُكَ الْعَفْوَ وَ الْعافِيَةَ', '', 1, 'ثمّ تقول ثلاثاً: ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الأول : في التعقيبات العامّة', '', '', 2, 'اللّٰهُمَّ إِنِّى أَسْأَلُكَ الْعَفْوَ وَ الْعافِيَةَ وَ الْمُعافاةَ فِى الدُّنْيا وَالْآخِرَةِ.', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الأول : في التعقيبات العامّة', 'سجده شکر بعد از هر نماز', '', 1, 'و بدان كه سجده شكر بعد از نمازها مستحب مؤكد است و دعا و اذكار بسيار در آن حال وارد شده واز حضرت امام رضا عليه السّلام منقول است كه اگر خواهي صد مرتبه:', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الأول : في التعقيبات العامّة', '', '', 2, 'شُكْراً شُكْراً', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الأول : في التعقيبات العامّة', '', '', 1, 'بگو و اگر خواهي صد مرتبه:', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الأول : في التعقيبات العامّة', '', '', 2, 'عَفْواً عَفْواً', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الأول : في التعقيبات العامّة', '', '', 1, 'و نيز از آن حضرت منقول است كه كمتر آن چه در اين سجده گويند آن است كه سه مرتبه بگويند:', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الأول : في التعقيبات العامّة', '', '', 2, 'شُكْراً لِلَّه', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الأول : في التعقيبات العامّة', 'دعای رضیت بالله ربا و بمحمد (ص) نبیا', '', 1, '', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'تعقيب صلاة الظهر', '', '', 1, 'قل في تعقيب صلاة الظهر، كما في المتهجّد:', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'تعقيب صلاة الظهر', 'دعای لا إِلهَ إِلّا اللَّهُ الْعَظِيمُ الْحَلِيمُ', '', 2, 'لا إِلهَ إِلّا اللَّهُ الْعَظِيمُ الْحَلِيمُ، لا إِلهَ إِلّا اللَّهُ رَبُّ الْعَرْشِ الْكَرِيمُ، اَلْحَمْدُ لِلَّهِ رَبِّ الْعَالَمِينَ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'تعقيب صلاة الظهر', '', '', 2, 'اَللّهُمَّ إِنِّي أَسْأَلُكَ مُوجِبَاتِ رَحْمَتِكَ، وَ عَزَائِمَ مَغْفِرَتِكَ، وَ الْغَنِيمَةَ مِنْ كُلِّ بِرٍّ، وَ السَّلامَةَ مِنْ كُلِّ إِثْمٍ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'تعقيب صلاة الظهر', '', '', 2, 'اَللّهُمَّ لا تَدَعْ لِي ذَنْبًا إِلّا غَفَرْتَهُ، وَ لا هَمًّا إِلّا فَرَّجْتَهُ، وَ لا سُقْماً إِلّا شَفَيْتَهُ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'تعقيب صلاة الظهر', '', '', 2, 'وَ لا عَيْباً إِلّا سَتَرْتَهُ، وَ لا رِزْقاً إِلّا بَسَطْتَهُ، وَ لا خَوْفاً إِلّا آمَنْتَهُ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'تعقيب صلاة الظهر', '', '', 2, 'وَ لا سُوءً إِلّا صَرَفْتَهُ، وَ لا حَاجَةً هِيَ لَكَ رِضاً وَ لِيَ فِيهَا صَلاحٌ إِلّا قَضَيْتَهَا', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'تعقيب صلاة الظهر', '', '', 2, 'يَا أَرْحَمَ الرَّاحِمِينَ، آمِينَ رَبَّ الْعَالَمِينَ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'تعقيب صلاة الظهر', 'دعای بِاللَّهِ اعْتَصَمْتُ، وَ بِاللَّهِ أَثِقُ', '', 1, 'وتقول عشر مرّات: ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'تعقيب صلاة الظهر', '', '', 2, 'بِاللَّهِ اعْتَصَمْتُ، وَ بِاللَّهِ أَثِقُ، وَ عَلَى اللَّهِ أَتَوَكَّلُ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'تعقيب صلاة الظهر', '', '', 2, 'اللّٰهُمَّ إِنْ عَظُمَتْ ذُنُوبِى فَأَنْتَ أَعْظَمُ، وَإِنْ كَبُرَ تَفْرِيطِى فَأَنْتَ أَكْبَرُ، وَإِنْ دامَ بُخْلِى فَأَنْتَ أَجْوَدُ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'تعقيب صلاة الظهر', '', '', 2, 'اللّٰهُمَّ اغْفِرْ لِى عَظِيمَ ذُنُوبِى بِعَظِيمِ عَفْوِكَ، وَكَثِيرَ تَفْرِيطِى بِظَاهِرِ كَرَمِكَ، وَاقْمَعْ بُخْلِى بِفَضْلِ جُودِكَ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'تعقيب صلاة الظهر', '', '', 2, 'اللّٰهُمَّ مَا بِنَا مِنْ نِعْمَةٍ فَمِنْكَ لَاإِلٰهَ إِلّا أَنْتَ، أَسْتَغْفِرُكَ وَأَتُوبُ إِلَيْكَ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'تعقيب صلاة الظهر', 'صلوات و تعجیل فرج بعد از نماز صبح و ظهر', '', 1, 'روایت است که بعد از نماز ظهر و نماز صبح در روز جمعه و غیر جمعه بگویید:', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'تعقيب صلاة الظهر', '', '', 2, 'اللَّهُمَّ صَلِّ عَلَي مُحَمَّدٍ وَ آلِ مُحَمَّدٍ وَ عَجِّلْ فَرَجَهُمْ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'تعقيب صلاة الظهر', '', '', 1, 'نميرد تا حضرت قائم (عج) را دريابد و اگر صد مرتبه اين صلوات را بخواند حق تعالي شصت حاجت او را برآورد سي از حاجات دنيا و سي از حاجات آخرت.', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'تعقيب صلاة الظهر', 'صلوات فرستادن بین نماز ظهر و عصر در روز جمعه ', '', 1, 'و نيز روايت است كه صلوات بر محمد و آل محمد در ما بين ظهر و عصر برابر است با هفتاد حج', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'تعقيب صلاة العصر', 'دعای أَسْتَغْفِرُ اللّٰهَ الَّذِى لَاإِلٰهَ إِلّا هُوَ الْحَىُّ الْقَيُّومُ', '', 2, 'أَسْتَغْفِرُ اللّٰهَ الَّذِى لَاإِلٰهَ إِلّا هُوَ الْحَىُّ الْقَيُّومُ الرَّحْمٰنُ الرَّحِيمُ، ذُو الْجَلالِ وَالْإِكْرامِ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'تعقيب صلاة العصر', '', '', 2, 'وَ أَسْألُهُ أَنْ يَتُوبَ عَلَىَّ تَوْبَةَ عَبْدٍ ذَلِيلٍ خاضِعٍ فَقِيرٍ، بَائِسٍ مِسْكِينٍ مُسْتَكِينٍ مُسْتَجِيرٍ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'تعقيب صلاة العصر', '', '', 2, 'لَايَمْلِكُ لِنَفْسِهِ نَفْعاً وَلَا ضَرَّاً وَلَا مَوْتاً وَلَا حَيَاةً وَلَا نُشُوراً', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'تعقيب صلاة العصر', '', '', 2, 'ثم تقول: ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'تعقيب صلاة العصر', 'دعای اللّٰهُمَّ إِنِّى أَعُوذُ بِكَ مِنْ نَفْسٍ لَاتَشْبَعُ', '', 2, 'اللّٰهُمَّ إِنِّى أَعُوذُ بِكَ مِنْ نَفْسٍ لَاتَشْبَعُ، وَمِنْ قَلْبٍ لَايَخْشَعُ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'تعقيب صلاة العصر', '', '', 2, 'وَمِنْ عِلْمٍ لَايَنْفَعُ، وَمِنْ صَلاةٍ لَاتُرْفَعُ، وَمِنْ دُعاءٍ لَايُسْمَعُ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'تعقيب صلاة العصر', '', '', 2, 'اللّٰهُمَّ إِنِّى أَسْأَلُكَ الْيُسْرَ بَعْدَ الْعُسْرِ، وَالْفَرَجَ بَعْدَ الْكَرْبِ، وَالرَّخاءَ بَعْدَ الشِّدَةِ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'تعقيب صلاة العصر', '', '', 2, 'اللّٰهُمَّ مَا بِنَا مِنْ نِعْمَةٍ فَمِنْكَ، لَاإِلٰهَ إِلّا أَنْتَ، أَسْتَغْفِرُكَ وَأَتُوبُ إِلَيْكَ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'تعقيب صلاة العصر', 'استغفار بعد از نماز عصر ', '', 1, 'وعن الصّادق عليه‌السلام قال: من استغفر الله تعالىٰ بعد صلاة العصر سبعين مرّة غفر الله له سبعمئة ذنب.', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'تعقيب صلاة العصر', 'تلاوت سوره ی قدر بعد از نماز عصر', '', 1, 'وروي عن أبي جعفر الثاني [الإمام محمّد التقي] عليه‌السلام قال: من قرأ (إِنَّا أَنزَلْنَاهُ فِي لَيْلَةِ الْقَدْرِ) بعد العصر عشر مرّات، مرّت له علىٰ مثل أعمال الخلائق في ذلك اليوم ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'تعقيب صلاة العصر', 'خواندن دعای عشرات', '', 1, 'ويستحبّ دعاء العشرات في كلّ صباحٍ ومساءٍ، وأفضل أوقاته بعد العصر يوم الجمعة. وسيأتي الدعاء فيما بعد ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'تعقيب صلاة المغرب', 'تلاوت آیه صلوات بعد از نماز عصر', '', 1, 'تقول بعد تسبيح الزهراء عليها‌السلام: ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'تعقيب صلاة المغرب', '', '', 2, 'إِنَّ اللّٰهَ وَمَلائِكَتَهُ يُصَلُّونَ عَلَى النَّبِيِّ، يَا أَيُّهَا الَّذِينَ آمَنُوا صَلُّوا عَلَيْهِ وَسَلِّمُوا تَسْلِيماً', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'تعقيب صلاة المغرب', '', '', 2, 'اللّٰهُمَّ صَلِّ عَلىٰ مُحَمَّدٍ النَّبِيِّ، وَعَلىٰ ذُرِّيَّتِهِ وَعَلىٰ أَهْلِ بَيْتِهِ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'تعقيب صلاة المغرب', 'بسمله و حوقله بعد از نماز مغرب', '', 1, 'ثمّ تقول سبع مرّات: ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'تعقيب صلاة المغرب', '', '', 2, 'بِسْمِ اللّهِ الرَّحْمَنِ الرَّحيمِ، وَ لا حَوْلَ وَ لا قُوَّةَ إِلّا بِاللّهِ الْعَلِيِّ الْعَظِيمِ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'تعقيب صلاة المغرب', 'دعای الْحَمْدُ لِلّهِ الَّذِي يَفْعَلُ مَا يَشَاءُ', '', 1, 'وثلاثاً: ', 'لینک'),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'تعقيب صلاة المغرب', '', '', 2, 'الْحَمْدُ لِلّهِ الَّذِي يَفْعَلُ مَا يَشَاءُ، وَ لا يَفْعَلُ مَا يَشَاءُ غَيْرُهُ.', 'لینک'),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'تعقيب صلاة المغرب', 'دعای سُبْحَانَكَ لا إِلهَ إِلّا أَنْتَ', '', 2, 'سُبْحَانَكَ لا إِلهَ إِلّا أَنْتَ، اغْفِرْ لِي ذُنُوبِي كُلَّهَا جَمِيعاً، فَإِنَّهُ لا يَغْفِرُ الذُّنُوبَ كُلَّهَا جَمِيعاً إِلّا أَنْتَ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'تعقيب صلاة المغرب', 'نافله مغرب', '', 1, ' ثمّ تصلّي نافلة المَغرب وهي أربع ركعات بسلامين، ولا تتكلّم بينهما بشيء.
وقال الشيخ: روي أنّه يقرأ في الركعة الاُولىٰ: سورة (قُلْ يَا أَيُّهَا الْكَافِرُونَ)، وفي 
الثانية: (قُلْ هُوَ اللَّهُ أَحَدٌ)، ويقرأ في الأُخريين ما شاء. وروي أنّ الإمام عليّ النقي عليه‌السلام كان يقرأ 
في الركعة الثالثة: سورة الحمد وأوّل سورة الحديد إلى: (وَهُوَ عَلِيمٌ بِذَاتِ الصُّدُورِ)، وفي 
الرابعة الحمد وآخر سورة الحشر، أي من: (لَوْ أَنزَلْنَا هَٰذَا الْقُرْآنَ) إلىٰ آخر السورة. ويستحبّ 
أن تقول في السجدة الأخيره من النوافل في كلّ ليلة، سيّما في ليلة الجمعة، سبع مرات:', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'تعقيب صلاة المغرب', 'دعای مَا شَاءَ اللّهُ، لا قُوَّةَ إِلّا بِاللّهِ', '', 2, 'مَا شَاءَ اللّهُ، لا قُوَّةَ إِلّا بِاللّهِ، أَسْتَغْفِرُ اللّهَ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'تعقيب صلاة المغرب', 'دعای اللّٰهُمَّ إِنِّى أَسْأَلُكَ مُوجِبَاتِ رَحْمَتِكَ', '', 2, 'اللّٰهُمَّ إِنِّى أَسْأَلُكَ مُوجِبَاتِ رَحْمَتِكَ، وَعَزائِمَ مَغْفِرَتِكَ، وَالنَّجاةَ مِنَ النَّارِ، وَ مِنْ كُلِّ بَلِيَّةٍ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'تعقيب صلاة المغرب', '', '', 2, 'وَالْفَوْزَ بِالْجَنَّةِ، وَالرِّضْوانِ [الرِّضْوانَ‌] فِى دارِ السَّلامِ، وَجِوَارَِ نَبِيِّكَ مُحَمَّدٍ عَلَيْهِ وَآلِهِ السَّلامُ', 'لینک'),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'تعقيب صلاة المغرب', '', '', 2, 'اللّٰهُمَّ مَا بِنَا مِنْ نِعْمَةٍ فَمِنْكَ، لَاإِلٰهَ إِلّا أَنْتَ، أَسْتَغْفِرُكَ وَأَتُوبُ إِلَيْكَ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'تعقيب صلاة المغرب', 'نماز غفیله', '', 1, 'وتصلّي الغُفَيلة بين المغرب والعشاء، وهي ركعتان تقرأ بعد الحمد في الأُولىٰ: ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'تعقيب صلاة المغرب', '', '', 2, 'وَ ذَا النُّونِ إِذْ ذَهَبَ مُغٰاضِباً فَظَنَّ أَنْ لَنْ نَقْدِرَ عَلَيْهِ فَنٰادىٰ فِي الظُّلُمٰاتِ أَنْ لاٰ إِلٰهَ إِلاّٰ أَنْتَ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'تعقيب صلاة المغرب', '', '', 2, 'سُبْحٰانَكَ إِنِّي كُنْتُ مِنَ الظّٰالِمِينَ، فَاسْتَجَبْنٰا لَهُ وَ نَجَّيْنٰاهُ مِنَ الْغَمِّ وَ كَذٰلِكَ نُنْجِي الْمُؤْمِنِينَ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'تعقيب صلاة المغرب', '', '', 1, 'وفي الثانية بعد الحمد: ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'تعقيب صلاة المغرب', '', '', 2, ' وَعِندَهُ مَفَاتِحُ الْغَيْبِ لَا يَعْلَمُهَا إِلَّا هُوَ وَيَعْلَمُ مَا فِي الْبَرِّ وَالْبَحْرِ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'تعقيب صلاة المغرب', '', '', 2, 'وَ مٰا تَسْقُطُ مِنْ وَرَقَةٍ إِلاّٰ يَعْلَمُهٰا وَ لاٰ حَبَّةٍ فِي ظُلُمٰاتِ الْأَرْضِ وَ لاٰ رَطْبٍ وَ لاٰ يٰابِسٍ إِلاّٰ فِي كِتٰابٍ مُبِينٍ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'تعقيب صلاة المغرب', '', '', 1, ' ثمّ تأخذ يديك للقنوت وتقول:', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'تعقيب صلاة المغرب', '', '', 2, 'اللّهُمَّ إِنِّي أَسْأَلُكَ بِمَفَاتِحِ الْغَيْبِ، الَّتِي لا يَعْلَمُهَا إِلّا أَنْتَ، أَنْ تُصَلِّيَ عَلَى مُحَمَّدٍ وَ آلِهِ وَ أَنْ تَفْعَلَ بِي كَذَا وَ كَذَا', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'تعقيب صلاة المغرب', '', '', 1, 'وتذكر حاجتك عوض هذه الكلمة، ثمّ تقول: ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'تعقيب صلاة المغرب', '', '', 2, 'اللّهُمَّ أَنْتَ وَلِيُّ نِعْمَتِي، وَ الْقَادِرُ عَلَى طَلِبَتِي، تَعْلَمُ حَاجَتِي', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'تعقيب صلاة المغرب', '', '', 2, 'فَأَسْأَلُكَ بِحَقِّ مُحَمَّدٍ وَ آلِهِ عَلَيْهِ وَ عَلَيْهِمُ السَّلامُ، لَمَّا قَضَيْتَهَا لِي.', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'تعقيب صلاة المغرب', '', '', 1, 'وتسأل حاجتك، فقد روي أنّ من أتىٰ بهذه الصلاة وسأل الله حاجته أعطاه الله ما سأل', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'تعقيب صلاة المغرب', 'بسمله و حوقله بعد از نماز صبح و مغرب', '', 1, 'و شيخ كليني از حضرت صادق (عليه السّلام) روايت كرده كه هر كه بعد از نماز صبح و نماز مغرب بگويد هفت مرتبه:', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'تعقيب صلاة المغرب', '', '', 2, 'بِسْمِ اللَّهِ الرَّحْمَنِ الرَّحِيمِ لاَ حَوْلَ وَ لاَ قُوَّهَ إلاَّ بِاللَّهِ الْعَلِيِّ	الْعَظِيمِ ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'تعقيب صلاة المغرب', '', '', 1, 'حق تعالي دفع كند از او هفتاد نوع از انواع بلا كه آسانتر آنها باد و پيسي و ديوانگي باشد و اگر شقي باشد محو شود از اشقيا و نوشته شود از سعدا.', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'تعقيب صلاة المغرب', 'دعا برای دنیا و آخرت بعد از نماز صبح و مغرب', '', 1, 'و نيز از آن حضرت روايت كرده كه براي دنيا و آخرت و رفع درد چشم اين دعا را بعد از نماز صبح و مغرب بخوانند:', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'تعقيب صلاة المغرب', '', '', 2, 'اللَّهُمَّ إِنِّي أَسْأَلُكَ بِحَقِّ مُحَمَّدٍ وَ آلِ مُحَمَّدٍ عَلَيْكَ ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'تعقيب صلاة المغرب', '', '', 2, 'صَلِّ عَلَي مُحَمَّدٍ وَ آلِ مُحَمَّدٍ ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'تعقيب صلاة المغرب', '', '', 2, 'وَ اجْعَلِ النُّورَ فِي بَصَرِي وَ الْبَصِيرَهَ فِي دِينِي ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'تعقيب صلاة المغرب', '', '', 2, 'وَ الْيَقِينَ فِي قَلْبِي وَ الإِْخْلاَصَ فِي عَمَلِي ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'تعقيب صلاة المغرب', '', '', 2, 'وَ السَّلاَمَهَ فِي نَفْسِي وَ السَّعَهَ فِي رِزْقِي وَ الشُّكْرَ لَكَ أَبَداً مَا	أَبْقَيْتَنِي', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'تعقيب صلاة العشاء', 'دعای سجده  ی آخر نوافل در هر شب', '', 1, 'و مستحب است در سجده آخر نافله ها در هر شب، و به ويژه در شب جمعه، هفت مرتبه خوانده شود:', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'تعقيب صلاة العشاء', '', '', 2, 'اَللَّهُمَّ إِنِّي أَسْأَلُكَ بِوَجْهِكَ الْكَرِيمِ وَ اسْمِكَ الْعَظِيمِ وَ مُلْكِكَ الْقَدِيمِ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'تعقيب صلاة العشاء', '', '', 2, 'أَنْ تُصَلِّيَ عَلَي مُحَمَّدٍ وَ آلِهِ وَ أَنْ تَغْفِرَ لِي ذَنْبِيَ الْعَظِيمَ إِنَّهُ لاَ يَغْفِرُ الْعَظِيمَ إلاَّ الْعَظِيمُ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'تعقيب صلاة العشاء', 'دعای رزق و روزی بعد از نماز عشاء', '', 2, 'اللّٰهُمَّ إِنَّهُ لَيْسَ لِى عِلْمٌ بِمَوْضِعِ رِزْقِى، وَإِنَّما أَطْلُبُهُ بِخَطَراتٍ تَخْطُرُ عَلَىٰ قَلْبِى، فَأَجُولُ فِى طَلَبِهِ الْبُلْدانَ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'تعقيب صلاة العشاء', '', '', 2, 'فَأَنَا فِيَما أَنَا طالِبٌ كَالْحَيْرانِ، لَاأَدْرِى أَفِى سَهْلٍ هُوَ أَمْ فِى جَبَلٍ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'تعقيب صلاة العشاء', '', '', 2, ' أَمْ فِى أَرْضٍ أَمْ فِى سَماءٍ، أَمْ فِى بَرٍّ أَمْ فِى بَحْرٍ، وَعَلَىٰ يَدَيْ مَنْ، وَمِنْ قِبَلِ مَنْ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'تعقيب صلاة العشاء', '', '', 2, 'وَقَدْ عَلِمْتُ أَنَّ عِلْمَهُ عِنْدَكَ، وَأَسْبابَهُ بِيَدِكَ، وَ أَنْتَ الَّذِى تَقْسِمُهُ بِلُطْفِكَ، وَتُسَبِّبُهُ بِرَحْمَتِكَ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'تعقيب صلاة العشاء', '', '', 2, 'اللّٰهُمَّ فَصَلِّ عَلىٰ مُحَمَّدٍ وَآلِهِ', 'از نرم افزار باب النعیم برداشته شد در سایت عرفان نبود'),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'تعقيب صلاة العشاء', '', '', 2, 'وَاجْعَلْ يا رَبِّ رِزْقَكَ لِى وَاسِعاً، وَمَطْلَبَهُ سَهْلاً، وَمَأْخَذَهُ قَرِيباً', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'تعقيب صلاة العشاء', '', '', 2, ' وَلَا تُعَنِّنِى بِطَلبِ مَا لَمْ تُقَدِّرْ لِى فِيهِ رِزْقاً', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'تعقيب صلاة العشاء', '', '', 2, ' فَإِنَّكَ غَنِيٌّ عَنْ عَذَابِي وَ أَنَا فَقِيرٌ إِلَىٰ رَحْمَتِكَ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'تعقيب صلاة العشاء', '', '', 2, 'فَصَلِّ عَلىٰ مُحَمَّدٍ وَآلِهِ، وَجُدْ عَلَىٰ عَبْدِكَ بِفَضْلِكَ، إِنَّكَ ذُو فَضْلٍ عَظِيمٍ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'تعقيب صلاة العشاء', 'تلاوت سوره ی قدر بعد از نماز عشاء', '', 1, 'أقول: هذا من أدعية الرزق، ويستحبّ أيضاً أن يقرأ عقيب العشاء: سورة (إِنَّا أَنزَلْنَاهُ) سبع مرّات: ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'تعقيب صلاة العشاء', 'نماز وتیره', '', 1, 'وأن يُقرأ في الوتيرة وهي ركعتان جالساً بعد العشاء مئة آية من القرآن، ويُستحبّ أن يُعتاض عن المئة آية سورة (إِذَا وَقَعَتِ الْوَاقِعَةُ) في ركعة، وسورة (قُلْ هُوَ اللَّهُ أَحَدٌ) في الركعة الأُخرىٰ.', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'تعقيب صلاة الصبح', 'صلوات بعد از نماز صبح ', '', 2, 'اللّٰهُمَّ صَلِّ عَلىٰ مُحَمَّدٍ وَآلِ مُحَمَّدٍ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'تعقيب صلاة الصبح', '', '', 2, ' وَاهْدِنِى لِمَا اخْتُلِفَ فِيهِ مِنَ الْحَقِّ بِإذْنِكَ، إِنَّكَ تَهْدِى مَنْ تَشَاءُ إِلىٰ صِرَاطٍ مُسْتَقِيمٍ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'تعقيب صلاة الصبح', 'صلوات بعد از نماز صبح و عصر روز جمعه  ', '', 1, 'وتقول عشر مرّات: ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'تعقيب صلاة الصبح', '', '', 2, 'اللّٰهُمَّ صَلِّ عَلىٰ مُحَمَّدٍ وَآلِ مُحَمَّدٍ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'تعقيب صلاة الصبح', '', '', 2, 'الْأَوْصِياءِ الرَّاضِينَ الْمَرْضِيِّينَ بِأَفْضَلِ صَلَواتِكَ، وَبارِكْ عَلَيْهِمْ بِأَفْضَلِ بَرَكَاتِكَ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'تعقيب صلاة الصبح', '', '', 2, 'وَالسَّلامُ عَلَيْهِمْ وَعَلىٰ أَرْواحِهِمْ وَأَجْسَادِهِمْ وَرَحْمَةُ اللّٰهِ وَبَرَكَاتُهُ', 'لینک'),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'تعقيب صلاة الصبح', '', '', 1, 'وهذه الصلاة واردة يوم الجمعة أيضاً عصراً بفضل عظيم.', 'لینک'),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'تعقيب صلاة الصبح', 'دعای اللّٰهُمَّ أَحْيِنِى عَلىٰ مَا أَحْيَيْتَ عَلَيْهِ عَلِىَّ (ع)', '', 2, 'اللّٰهُمَّ أَحْيِنِى عَلىٰ مَا أَحْيَيْتَ عَلَيْهِ عَلِىَّ بْنَ أَبِى طَالِبٍ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'تعقيب صلاة الصبح', '', '', 2, ' وَأَمِتْنِى عَلَىٰ مَا ماتَ عَلَيْهِ عَلِىُّ بْنُ أَبِى طالِبٍ عَلَيْهِ السَّلامُ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'تعقيب صلاة الصبح', 'استغفار و صلوات و تلاوت سوره اخلاص بعد از نماز صبح ', '', 1, 'وقل مئة مرّة: ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'تعقيب صلاة الصبح', '', '', 2, 'أَسْتَغْفِرُ اللّهَ وَ أَتُوبُ إِلَيْهِ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'تعقيب صلاة الصبح', '', '', 1, 'ومئة مرّة: ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'تعقيب صلاة الصبح', '', '', 2, 'أَسْأَلُ اللّهَ الْعَافِيَةَ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'تعقيب صلاة الصبح', '', '', 1, 'ومئة مرّة: ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'تعقيب صلاة الصبح', '', '', 2, 'أَسْتَجِيرُ بِاللّهِ مِنَ الْنَّارِ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'تعقيب صلاة الصبح', '', '', 1, 'ومئة مرّة: ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'تعقيب صلاة الصبح', '', '', 2, 'وَ أَسْأَلُهُ الْجَنَّةَ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'تعقيب صلاة الصبح', '', '', 1, 'ومئة مرّة: ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'تعقيب صلاة الصبح', '', '', 2, 'أَسْأَلُ اللّهَ الْحُورَ الْعِينَ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'تعقيب صلاة الصبح', '', '', 1, 'ومئة مرّة: ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'تعقيب صلاة الصبح', '', '', 2, 'لا إِلهَ إِلّا اللّهُ الْمَلِكُ الْحَقُّ الْمُبِينُ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'تعقيب صلاة الصبح', '', '', 1, 'ومئة مرة: التوحيد. ومئة مرّة: ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'تعقيب صلاة الصبح', '', '', 2, 'صَلَّى اللَّهُ عَلَى مُحَمَّدٍ وَ آلِ مُحَمَّدٍ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'تعقيب صلاة الصبح', '', '', 1, 'ومئة مرّة: ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'تعقيب صلاة الصبح', '', '', 2, 'سُبْحَانَ اللّٰهِ، وَالْحَمْدُ للّٰهِ، وَلَا إِلٰهَ إِلّا اللّٰهُ، وَاللّٰهُ أَكْبَرُ، وَلَا حَوْلَ وَلَا قُوَّةَ إِلّا بِاللّٰهِ الْعَلِيِّ الْعَظِيمِ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'تعقيب صلاة الصبح', '', '', 1, 'ومئة مرّة: ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'تعقيب صلاة الصبح', '', '', 2, 'مَا شَاءَ اللّٰهُ كَانَ، وَ لَا حَوْلَ وَلَا قُوَّةَ إِلّا بِاللّٰهِ الْعَلِىِّ الْعَظِيمِ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'تعقيب صلاة الصبح', 'دعای امام علی (ع) در لیله المبیت', '', 2, 'أَصْبَحْتُ اللّٰهُمَّ مُعْتَصِماً بِذِمَامِكَ الْمَنِيعِ الَّذِى لَايُطاوَلُ وَلَا يُحاوَلُ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'تعقيب صلاة الصبح', '', '', 2, 'مِنْ شَرِّ كُلِّ غاشِمٍ وَ طَارِقٍ مِنْ سَائِرِ مَنْ خَلَقْتَ وَ مَا خَلَقْتَ مِنْ خَلْقِكَ الصَّامِتِ وَالنَّاطِقِ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'تعقيب صلاة الصبح', '', '', 2, 'فِى جُنَّةٍ مِنْ كُلِّ مَخُوفٍ، بِلِبَاسٍ سَابِغَةٍ وَِلَاءِ أَهْلِ بَيْتِ نَبِيِّكَ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'تعقيب صلاة الصبح', '', '', 2, 'مُحْتَجِباً مِنْ كُلِّ قاصِدٍ لِى إِلىٰ أَذِيَّةٍ، بِجِدَارٍ حَصِينِ الإِخْلَاصِ فى الاعْتِرافِ بِحَقِّهِمْ، وَالتَّمَسُّكِ بِحَبْلِهِمْ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'تعقيب صلاة الصبح', '', '', 2, 'مُوقِناً أَنَّ الْحَقَّ لَهُمْ وَ مَعَهُمْ وَ فِيهِمْ وَ بِهِمْ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'تعقيب صلاة الصبح', '', '', 2, 'أُوالِى مَنْ وَالَوْا، وَ أُجانِبُ مَنْ جَانَبُوا', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'تعقيب صلاة الصبح', '', '', 2, ' فَأَعِذْنِى اللّٰهُمَّ بِهِمْ مِنْ شَرِّ كُلِّ مَا أَتَّقِيهِ يَا عَظِيمُ، حَجَزْتُ الْأَعادِىَ عَنِّى بِبَدِيعِ السَّمٰواتِ وَالْأَرْضِ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'تعقيب صلاة الصبح', '', '', 2, 'إِنَّا جَعَلْنا مِنْ بَيْنِ أَيْدِيهِمْ سَدّاً وَمِنْ خَلْفِهِمْ سَدّاً فَأَغْشَيْناهُمْ فَهُمْ لَايُبْصِرُوُنَ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'تعقيب صلاة الصبح', '', '', 1, 'وهذا دعاء يدعىٰ به في كلّ صباح ومساء (3)، وهو دعاء أميرالمؤمنين عليه‌السلام ليلة المبيت ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'تعقيب صلاة الصبح', 'تسبیح و حو قله بعد از نماز صبح ', '', 1, 'وروي في التهذيب أنّ من قال بعد فريضة الفجر عشر مرّات: ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'تعقيب صلاة الصبح', '', '', 2, 'سُبْحَانَ اللّٰهِ الْعَظِيمِ وَبِحَمْدِهِ وَلَا حَوْلَ وَلَا قُوَّةَ إِلّا باللّٰهِ الْعَلِيِّ الْعَظِيمِ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'تعقيب صلاة الصبح', '', '', 1, 'عافاه الله تعالىٰ من العمىٰ والجنون والجذام والفقر والهدم (انهدام الدار) أو الهرم (الخرافة عند الهرم) ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'تعقيب صلاة الصبح', 'بسمله و حوقله بعد از نماز صبح و مغرب', '', 1, 'وروىٰ الكليني عن أبي عبد الله عليه‌السلام قال: من قال في دبر صلاة الفجرودبر صلاة المغرب سبع مرّات: ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'تعقيب صلاة الصبح', '', '', 2, 'بِسْمِ اللّٰهِ الرَّحْمٰنِ الرَّحِيمِ، لَاحَوْلَ وَلَا قُوَّةَ إِلّا بِاللّٰهِ الْعَلِيِّ الْعَظِيمِ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'تعقيب صلاة الصبح', '', '', 1, 'دفع الله عنه سبعين نوعاً من أنواع البلاء أهونها الريح والبرص والجنون، وإنْ كان شقيّاً مُحي من الشقاء وكُتب في السعداء', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'تعقيب صلاة الصبح', 'دعا برای دنیا و آخرت بعد از نماز صبح و مغرب', '', 1, 'وروي عنه عليه‌السلام أيضاً للدنيا والآخرة، ولوجع العين هذا الدعاء بعد فريضتي الصبح والمغرب: ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'تعقيب صلاة الصبح', '', '', 2, 'اللّٰهُمَّ إِنِّى أَسْأَلُكَ بِحَقِّ مُحَمَّدٍ وَآلِ مُحَمَّدٍ عَلَيْكَ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'تعقيب صلاة الصبح', '', '', 2, 'صَلِّ عَلَىٰ مُحَمَّدٍ وَآلِ مُحَمَّدٍ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'تعقيب صلاة الصبح', '', '', 2, 'واجْعَلِ النُّورَ فِى بَصَرِى، وَالْبَصِيرَةَ فِى دِينِى', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'تعقيب صلاة الصبح', '', '', 2, 'وَالْيَقِينَ فِى قَلْبِى، وَالْإِخْلاصَ فِى عَمَلِى', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'تعقيب صلاة الصبح', '', '', 2, 'وَالسَّلامَةَ فِى نَفْسِى، وَالسَّعَةَ فِى رِزْقِى، وَالشُّكْرَ لَكَ أَبَداً مَا أَبْقَيْتَنِى', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'حكاية نافعة لحلّ المشاكل', 'دعای افوض امری الی الله بعد از نماز صبح ', '', 1, 'أقول: روى الشيخ ابن فهد في عدّة الدّاعي عن الرضا عليه‌السلام: من قال في دبر صلاة الغداة لم يلتمس حاجة إلّا تيسّرت له، وكفاه الله ما أهمّه:', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'حكاية نافعة لحلّ المشاكل', '', '', 2, 'بِسْمِ اللّٰهِ وَصَلَّى اللّٰهُ عَلى مُحَمَّدٍ وَآلِهِ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'حكاية نافعة لحلّ المشاكل', '', '', 2, 'وَأُفَوِّضُ أَمْرِى إِلَى اللّٰهِ إِنَّ اللّٰهَ بَصِيرٌ بِالْعِبادِ، فَوَقَاهُ اللّٰهُ سَيِّئاتِ مَا مَكَرُوا', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'حكاية نافعة لحلّ المشاكل', '', '', 2, 'لَاإِلٰهَ إِلّا أَنْتَ سُبْحانَكَ إِنِّى كُنْتُ مِنَ الظَّالِمِينَ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'حكاية نافعة لحلّ المشاكل', '', '', 2, 'فَاسْتَجَبْنَا لَهُ وَ نَجَّيْناهُ مِنَ الْغَمِّ وَكَذَلِكَ نُنْجِى الْمُؤْمِنِينَ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'حكاية نافعة لحلّ المشاكل', '', '', 2, 'حَسْبُنَا اللّٰهُ وَنِعْمَ الْوَكيلُ، فَانْقَلَبُوا بِنِعْمةٍ مِنَ اللّٰهِ وَفَضْلٍ لَمْ يَمْسَسْهُمْ سُوءٌ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'حكاية نافعة لحلّ المشاكل', '', '', 2, 'مَا شَاءَ اللّٰهُ لَا حَوْلَ وَلَا قُوَّةَ إِلّا بِاللّٰهِ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'حكاية نافعة لحلّ المشاكل', '', '', 2, 'مَا شَاءَ اللّٰهُ لَامَا شَاءَ النَّاسُ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'حكاية نافعة لحلّ المشاكل', '', '', 2, 'مَا شَاءَ اللّٰهُ وَإِنْ كَرِهَ النَّاسُ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'حكاية نافعة لحلّ المشاكل', '', '', 2, 'حَسْبِىَ الرَّبُّ مِنَ الْمَرْبُوبِينَ، حَسْبِىَ الْخالِقُ مِنَ الْمَخْلُوقِينَ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'حكاية نافعة لحلّ المشاكل', '', '', 2, 'حَسْبِىَ الرَّازِقُ مِنَ الْمَرْزُوقِينَ، حَسْبِىَ اللّٰهُ رَبُّ الْعالَمِينَ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'حكاية نافعة لحلّ المشاكل', '', '', 2, 'حَسْبِى مَنْ هُوَ حَسْبِى، حَسْبِى مَنْ لَمْ يَزَلْ حَسْبِى', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'حكاية نافعة لحلّ المشاكل', '', '', 2, 'حَسْبِى مَنْ كَانَ مُذْ كُنْتُ لَمْ يَزَلْ حَسْبِى، حَسْبِىَ اللّٰهُ، لَاإِلٰهَ إِلّا هُوَ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'حكاية نافعة لحلّ المشاكل', '', '', 2, 'عَلَيْهِ تَوَكَّلْتُ، وَهُوَ رَبُّ الْعَرْشِ الْعَظِيمِ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'حكاية نافعة لحلّ المشاكل', '', '', 1, 'أقول: حكىٰ شيخنا ثقة الإسلام النوري نوّر الله مرقده في كتاب دار السلام عن شيخه المَرحوم العالم الرّباني الحاجّ المولىٰ فتح علي السلطان آبادي رحمه‌الله، أنّ الآخوند المولىٰ محمّد صادق العراقي كان في غاية الضيق والعسرة والضرّاء، ومضىٰ عليه كذلك زمان، فلم يجد من كربه فرجاً، ولا من ضيقه مخرجاً، إلىٰ أن رأىٰ ليلة في المنام كأنّه في وادٍ يتراءىٰ فيه خيمة عظيمة عليها قبّة، فسأل عن صاحبها، فقيل فيها الكهف الحصين، وغياث المضطرّ المستكين، الحجّة القائم المهديّ المنتظر عجّل الله تعالىٰ فرجه، فأسرع الذهاب إليها، فلمّا وافاه صلوات الله عليه شكا عنده سوء حاله، وسأل عنه دعاءً يفرّج به همّه، ويدفع به غمّه، فأحاله عليه‌السلام إلىٰ سيّد من ولْده وإلىٰ خيمته، فخرج من حضرته ودخل في تلك الخيمة، فرأىٰ السيّد السند، والحبر المعتمد، العالم الأمجد المؤيّد جناب السيّد محمّد السلطان آبادي قاعداً علىٰ سجّادته، مشغولاً بدعائه وقراءته، فذكر له بعد السلام ما أحال عليه حجّة الملك العلّام، فعلّمه دعاءً يستكفي به ضيقه، ويستجلب به رزقه، فانتبه من نومه والدعاء محفوظ في خاطره، فقصد بيت جناب السيّد وكان قبل تلك الرؤيا نافراً عنه لوجهٍ لا يذكره، فلمّا أَتاه ودخل عليه رآه كما في النوم علىٰ مصلَّاه، ذاكراً
ربّه، مستغفراً ذنبه، فلمّا سلّم عليه أجابه وتبسّم في وجهه كأنّه عرف القضيّة، فسأل عنه ما سأل عنه في الرؤيا، فعلّمه من حينه عين ذاك الدّعاء، فدعا به في قليل من الزمان، فصبّت عليه الدنيا من كلّ ناحية ومكان. وكان المَرحوم الحاجّ المولىٰ فتح علي رحمه‌الله يثني علىٰ السيّد ثناءً بليغاً، وقد أدركه في أواخر عمره وتلمَّذ عليه شطراً من الزمان، وأمّا ما علّمه السيّد في اليقظة والمنام فثلاثة أُمور:الأوّل: أن يذكر عقيب الفجر سبعين مرّة واضعاً يده علىٰ صدره: يا فتّاحُ.الثاني: أن يواظب علىٰ هذا الدعاء المرويّ في الكافي، وقد علّمه النبي صلى‌الله‌عليه‌وآله‌وسلم رجلاً من 
أصحابه مُبتلى بالسقم والفقر، فما لبث أن ذهب عنه السقم والفقر:', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'حكاية نافعة لحلّ المشاكل', '', '', 2, 'لا حَولَ وَ لا قُوَّهَ اِلاّ بِاللهِ ؛ تَوَكَّلتُ عَلَي الحَيِّ الَّذي لا يَموتُ ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'حكاية نافعة لحلّ المشاكل', '', '', 2, 'وَ الحَمدُ لِله الَّذي لَم يَتَّخِذ وَلَدًا ، وَ لَم يَكُن لَه{ شَريكٌ فِي	المُلكِ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'حكاية نافعة لحلّ المشاكل', '', '', 2, 'وَلَم يَكُن لَه{ وَلِيٌّ مِنَ الذُّلِّ؛ وَ كَبِّرهُ تَكبيرًا', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'حكاية نافعة لحلّ المشاكل', '', '', 1, 'الثالث: أن يدعو في دبر صلاة الغداة بالدعاء الذي رواه ابن فهد. وينبغي أن يغتنم هذه الأوراد ويداوم عليها ولا يغفل عن آثارها.', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'سجدة الشكر', '', '', 1, 'واعلم أنّه يستحبّ سجدة الشكر عقيب الصلوات استحباباً أكيداً، والدعوات والأذكار المأثورة فيها كثيرة. وقد روي عن الرضا عليه‌السلام قال: إن شئت فقل فيها مئة مرّة شُكْراً شُكْراً، وإن شئت فقل مئة مرّة عَفْواً عَفْواً. وعنه عليه‌السلام قال: أدنىٰ ما يجزي في سجدة الشكر أن يقول ثلاثاً: شُكْراً لِلّٰهِ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الدعاء عند طلوع الشمس وعند غروبها', 'تهلیل قبل از طلوع و غروب آفتاب ', '', 1, 'واعلَم أيضاً أنَّ لنا ادعيةً وأذكاراً كثيرةً واردة عند طلوع الشَمس وعند غروبها مأثورة عَن النَّبيِّ صلى‌الله‌عليه‌وآله والأئمة الطاهرين عليه‌السلام ، وَقَد حرَّضت الآيات والاَخبار تحريضاً ورغبت ترغيباً في المحافظة على هاتين الساعتين وَنَحنُ نقتصر هنا على ذكر عدة من الأدعية المُعتبرة.', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الدعاء عند طلوع الشمس وعند غروبها', 'تهلیل قبل از طلوع و غروب آفتاب ', '', 1, 'الأوّل: روىٰ مشايخ الحديث بأسانيد معتبره عن الصّادق عليه‌السلام أنّه قال: فريضة علىٰ كلّ مسلم أن يقول قبل طلوع الشمس عشراً، وقبل غروبها عشراً:', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الدعاء عند طلوع الشمس وعند غروبها', 'تهلیل قبل از طلوع و غروب آفتاب ', '', 2, 'لَا إِلٰهَ إِلّا اللّٰهُ وَحْدَهُ لَاشَرِيكَ لَهُ، لَهُ الْمُلْكُ وَلَهُ الْحَمْدُ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الدعاء عند طلوع الشمس وعند غروبها', 'تهلیل قبل از طلوع و غروب آفتاب ', '', 2, 'يُحْيِى وَيُمِيتُ، وَيُمِيتُ وَيُحْيِى ، وَهُوَ حَيٌّ لَايَمُوتُ، بِيَدِهِ الْخَيْرُ، وَهُوَ عَلَىٰ كُلِّ شَىْءٍ قَدِيرٌ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الدعاء عند طلوع الشمس وعند غروبها', 'تهلیل قبل از طلوع و غروب آفتاب ', '', 1, 'وورد في بعض الروايات أنّ ذلك يُقضىٰ قضاء إذا تُرك فإنّه لازم.', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الدعاء عند طلوع الشمس وعند غروبها', 'استعاذه قبل از طلوع و غروب آفتاب ', '', 1, 'الثاني: ورُوي بطرق معتبرة عنه عليه‌السلام أيضاً: قل قبل طلوع الشمس وقبل غروبها عشر مرّات: ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الدعاء عند طلوع الشمس وعند غروبها', 'استعاذه قبل از طلوع و غروب آفتاب ', '', 2, 'أَعُوذُ بِاللّٰهِ السَّمِيعِ الْعَلِيمِ مِنْ هَمَزَاتِ الشَّيَاطِينِ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الدعاء عند طلوع الشمس وعند غروبها', 'استعاذه قبل از طلوع و غروب آفتاب ', '', 2, ' وَأَعُوذُ بِاللّٰهِ أَنْ يَحْضُرُونِ، إِنَّ اللّٰهَ هُوَ السَّمِيعُ الْعَلِيمُ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الدعاء عند طلوع الشمس وعند غروبها', 'دعای اللّٰهُمَّ مُقَلِّبَ الْقُلُوبِ وَالْأَبْصَارِ', '', 1, 'الثالث: أيضاً عنه عليه‌السلام قال: ما يمنعكم أن تقولوا في كلّ صباح ومساء ثلاث مرّات:', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الدعاء عند طلوع الشمس وعند غروبها', 'دعای اللّٰهُمَّ مُقَلِّبَ الْقُلُوبِ وَالْأَبْصَارِ', '', 2, 'اللّٰهُمَّ مُقَلِّبَ الْقُلُوبِ وَالْأَبْصَارِ ثَبِّتْ قَلْبِى عَلَىٰ دِينِكَ، وَلَا تُزِغْ قَلْبِى بَعْدَ إِذْ هَدَيْتَنِى', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الدعاء عند طلوع الشمس وعند غروبها', 'دعای اللّٰهُمَّ مُقَلِّبَ الْقُلُوبِ وَالْأَبْصَارِ', '', 2, 'وَهَبْ لِى مِنْ لَدُنْكَ رَحْمَةً، إِنَّكَ أَنْتَ الْوَهَّابُ، وَأَجِرْنِى مِنَ النَّارِ بِرَحْمَتِكَ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الدعاء عند طلوع الشمس وعند غروبها', 'دعای اللّٰهُمَّ مُقَلِّبَ الْقُلُوبِ وَالْأَبْصَارِ', '', 2, 'اللّٰهُمَّ امْدُدْ لِى فِى عُمْرِى، وَأَوْسِعْ عَلَىَّ فِى رِزْقِى، وَانْشُرْ عَلَىَّ رَحْمَتَكَ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الدعاء عند طلوع الشمس وعند غروبها', 'دعای اللّٰهُمَّ مُقَلِّبَ الْقُلُوبِ وَالْأَبْصَارِ', '', 2, ' وَ إِنْ كُنْتُ عِنْدَكَ فِى أُمِّ الْكِتابِ شَقِيّاً فَاجْعَلْنِى سَعِيداً، فَإِنَّكَ تَمْحُو مَا تَشَاءُ وَتُثْبِتُ، وَعِنْدَكَ أُمُّ الْكِتابِ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الدعاء عند طلوع الشمس وعند غروبها', 'ذکر الْحَمْدُ لِلّٰهِ الَّذِى يَفْعَلُ مَا يَشَاءُ، وَلَا يَفْعَلُ مَا يَشَاءُ', '', 1, 'الرابع: أيضاً عنه عليه‌السلام: قل في كلّ صباح ومساء: ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الدعاء عند طلوع الشمس وعند غروبها', 'ذکر الْحَمْدُ لِلّٰهِ الَّذِى يَفْعَلُ مَا يَشَاءُ، وَلَا يَفْعَلُ مَا يَشَاءُ', '', 2, 'الْحَمْدُ لِلّٰهِ الَّذِى يَفْعَلُ مَا يَشَاءُ، وَلَا يَفْعَلُ مَا يَشَاءُ غَيْرُهُ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الدعاء عند طلوع الشمس وعند غروبها', 'ذکر الْحَمْدُ لِلّٰهِ الَّذِى يَفْعَلُ مَا يَشَاءُ، وَلَا يَفْعَلُ مَا يَشَاءُ', '', 2, 'الْحَمْدُ لِلّٰهِ كَمَا يُحِبُّ اللّٰهُ أَنْ يُحْمَدَ، الْحَمْدُ لِلّٰهِ كَمَا هُوَ أَهْلُهُ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الدعاء عند طلوع الشمس وعند غروبها', 'ذکر الْحَمْدُ لِلّٰهِ الَّذِى يَفْعَلُ مَا يَشَاءُ، وَلَا يَفْعَلُ مَا يَشَاءُ', '', 2, 'اللّٰهُمَّ أَدْخِلْنِى فِى كُلِّ خَيْرٍ أَدْخَلْتَ فِيهِ مُحَمَّداً وَآلَ مُحَمَّدٍ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الدعاء عند طلوع الشمس وعند غروبها', 'ذکر الْحَمْدُ لِلّٰهِ الَّذِى يَفْعَلُ مَا يَشَاءُ، وَلَا يَفْعَلُ مَا يَشَاءُ', '', 2, ' وَأَخْرِجْنِى مِنْ كُلِّ شَرٍّ أَخْرَجْتَ مِنْهُ مَحَمَّداً وَآلَ مُحَمَّدٍ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الدعاء عند طلوع الشمس وعند غروبها', 'ذکر الْحَمْدُ لِلّٰهِ الَّذِى يَفْعَلُ مَا يَشَاءُ، وَلَا يَفْعَلُ مَا يَشَاءُ', '', 2, 'صَلَّى اللّٰهُ عَلىٰ مُحَمَّدٍ وَآلِ مُحَمَّدٍ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الدعاء عند طلوع الشمس وعند غروبها', 'ذکر سُبْحانَ الله', '', 1, 'الخامس : قل كل صباح ومساء عشر مرات :', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الدعاء عند طلوع الشمس وعند غروبها', 'ذکر سُبْحانَ الله', '', 2, 'سُبْحانَ الله وَالحَمْدُ للهِ وَلا إِلهَ إِلاّ الله وَالله أَكْبَرُ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الدعاء عند طلوع الشمس وعند غروبها', 'دعاء العشرات', '', 1, 'ومن دعوات الصباح والمساء دعاء العشرات', 'link'),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'دعاء يوم السبت', '', 2, 'بِسْمِ اللّهِ الرَّحْمنِ الرَّحِيمِ، بِسْمِ اللّٰهِ كَلِمَةِ الْمُعْتَصِمِينَ، وَمَقالَةِ الْمُتَحَرِّزِينَ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'دعاء يوم السبت', '', 2, 'وَأَعُوذُ بِاللّٰهِ تَعَالىٰ مِنْ جَوْرِ الْجَائِرِينَ، وَكَيْدِ الْحَاسِدِينَ، وَبَغْىِ الظَّالِمِينَ، وَأَحْمَدُهُ فَوْقَ حَمْدِ الْحَامِدِينَ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'دعاء يوم السبت', '', 2, 'اللّٰهُمَّ أَنْتَ الْواحِدُ بِلَا شَرِيكٍ، وَالْمَلِكُ بِلَا تَمْلِيكٍ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'دعاء يوم السبت', '', 2, 'لَاتُضَادُّ فِى حُكْمِكَ، وَلَا تُنَازَعُ فِى مُلْكِكَ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'دعاء يوم السبت', '', 2, 'أَسْأَلُكَ أَنْ تُصَلِّىَ عَلَىٰ مُحَمَّدٍ عَبْدِكَ وَرَسُولِكَ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'دعاء يوم السبت', '', 2, 'وَأَنْ تُوزِعَنِى مِنْ شُكْرِ نُعْمَاكَ مَا تَبْلُغُ بِى غَايَةَ رِضَاكَ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'دعاء يوم السبت', '', 2, 'وَأَنْ تُعِينَنِى عَلَىٰ طَاعَتِكَ وَلُزُومِ عِبَادَتِكَ، وَاسْتِحْقَاقِ مَثُوبَتِكَ بِلُطْفِ عِنَايَتِكَ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'دعاء يوم السبت', '', 2, 'وَتَرْحَمَنِى بِصَدِّى عَنْ مَعَاصِيكَ مَا أَحْيَيْتَنِى، وَتُوَفِّقَنِى لِمَا يَنْفَعُنِى مَا أَبْقَيْتَنِى', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'دعاء يوم السبت', '', 2, 'وَأَنْ تَشْرَحَ بِكِتَابِكَ صَدْرِى، وَتَحُطَّ بِتِلاوَتِهِ وِزْرِى', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'دعاء يوم السبت', '', 2, 'وَتَمْنَحَنِى السَّلَامَةَ فِى دِينِى وَنَفْسِى، وَلَا تُوحِشَ بِى أَهْلَ أُنْسِى', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'دعاء يوم السبت', '', 2, 'وَتُتِمَّ إِحْسَانَكَ فِيَما بَقِىَ مِنْ عُمْرِى كَمَا أَحْسَنْتَ فِيَما مَضَىٰ مِنْهُ، يَا أَرْحَمَ الرَّاحِمِينَ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'دعاء يوم الأحد', '', 2, 'بِسْمِ اللّهِ الرَّحْمنِ الرَّحِيمِ، بِسْمِ اللّٰهِ الَّذِى لَاأَرْجُو إِلّا فَضْلَهُ، وَلَا أَخْشىٰ إِلّا عَدْلَهُ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'دعاء يوم الأحد', '', 2, 'وَلَا أَعْتَمِدُ إِلّا قَوْلَهُ، وَلَا أُمْسِكُ إِلّا بِحَبْلِهِ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'دعاء يوم الأحد', '', 2, 'بِكَ أَسْتَجِيرُ يَا ذَا الْعَفْوِ وَالرِّضْوانِ مِنَ الظُّلْمِ وَالْعُدْوانِ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'دعاء يوم الأحد', '', 2, 'وَمِنْ غِيَرِ الزَّمانِ، وَتَواتُرِ الْأَحْزانِ، وَطَوارِقِ الْحَدَثانِ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'دعاء يوم الأحد', '', 2, ' وَمِنِ انْقِضَاءِ الْمُدَّةِ قَبْلَ التَّأَهُّبِ وَالْعُدَّةِ، وَ إِيَّاكَ أَسْتَرْشِدُ لِمَا فِيهِ الصَّلَاحُ وَالْإِصْلاحُ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'دعاء يوم الأحد', '', 2, ' وَبِكَ أَسْتَعِينُ فِيَما يَقْتَرِنُ بِهِ النَّجَاحُ وَ الإِنْجَاحُ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'دعاء يوم الأحد', '', 2, 'وَ إِيَّاكَ أَرْغَبُ فِى لِبَاسِ الْعافِيَةِ وَتَمامِها وَ شُمُولِ السَّلَامَةِ وَدَوامِهَا', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'دعاء يوم الأحد', '', 2, ' وَأَعُوذُ بِكَ يَا رَبِّ مِنْ هَمَزاتِ الشَّيَاطِينِ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'دعاء يوم الأحد', '', 2, 'وَ أَحْتَرِزُ بِسُلْطانِكَ مِنْ جَوْرِ السَّلَاطِينِ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'دعاء يوم الأحد', '', 2, 'فَتَقَبَّلْ مَا كَانَ مِنْ صَلَاتِى وَصَوْمِى', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'دعاء يوم الأحد', '', 2, 'وَاجْعَلْ غَدِى وَمَا بَعْدَهُ أَفْضَلَ مِنْ سَاعَتِى وَيَوْمِى', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'دعاء يوم الأحد', '', 2, ' وَأَعِزَّنِى فِى عَشِيرَتِى وَقَوْمِى، وَاحْفَظْنِى فِى يَقْظَتِى وَنَوْمِى', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'دعاء يوم الأحد', '', 2, ' فَأَنْتَ اللّٰهُ خَيْرٌ حَافِظاً، وَأَنْتَ أَرْحَمُ الرَّاحِمِينَ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'دعاء يوم الأحد', '', 2, ' اللّٰهُمَّ إِنِّى أَبْرَأُ إِلَيْكَ فِى يَوْمِى هٰذَا وَمَا بَعْدَهُ مِنَ الْآحادِ، مِنَ الشِّرْكِ وَالْإِلْحَادِ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'دعاء يوم الأحد', '', 2, 'وَأُخْلِصُ لَكَ دُعَائِى تَعَرُّضاً لِلْإِجَابَةِ، وَأُقِيمُ عَلَىٰ طَاعَتِكَ رَجَاءً لِلْإِثَابَةِ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'دعاء يوم الأحد', '', 2, 'فَصَلِّ عَلَىٰ مُحَمَّدٍ خَيْرِ خَلْقِكَ، الدَّاعِى إِلَىٰ حَقِّكَ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'دعاء يوم الأحد', '', 2, 'وَأَعِزَّنِى بِعِزِّكَ الَّذِى لَايُضَامُ، وَاحْفَظْنِى بِعَيْنِكَ الَّتِى لَاتَنَامُ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'دعاء يوم الأحد', '', 2, ' وَاخْتِمْ بِالانْقِطَاعِ إِلَيْكَ أَمْرِى، وَبِالْمَغْفِرَةِ عُمْرِى، إِنَّكَ أَنْتَ الْغَفُورُ الرَّحِيمُ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'دعاء يوم الاثنين', '', 2, 'بِسْمِ اللّهِ الرَّحْمنِ الرَّحِيمِ، الْحَمْدُ لِلّٰهِ الَّذِى لَمْ يُشْهِدْ أَحَداً حِينَ فَطَرَ السَّمَاوَاتِ وَالأَرْضَ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'دعاء يوم الاثنين', '', 2, ' وَلَا اتَّخَذَ مُعِيناً حِينَ بَرَأَ النَّسَمَات', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'دعاء يوم الاثنين', '', 2, 'لَمْ يُشَارَكْ فِى الْإِلٰهِيَّةِ، وَلَمْ يُظاهَرْ فِى الْوَحْدَانِيَّةِ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'دعاء يوم الاثنين', '', 2, 'كَلَّتِ الأَلْسُنُ عَنْ غَايَةِ صِفَتِهِ، وَالْعُقُولُ عَنْ كُنْهِ مَعْرِفَتِهِ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'دعاء يوم الاثنين', '', 2, 'وَتَوَاضَعَتِ الْجَبابِرَةُ لِهَيْبَتِهِ، وَعَنَتِ الْوُجُوهُ لِخَشْيَتِهِ، وَانْقَادَ كُلُّ عَظِيمٍ لِعَظَمَتِهِ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'دعاء يوم الاثنين', '', 2, 'فَلَكَ الْحَمْدُ مُتَواتِراً مُتَّسِقاً، وَمُتَوالِياً مُسْتَوْسِقاً', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'دعاء يوم الاثنين', '', 2, 'وَصَلَوَاتُهُ عَلَىٰ رَسُو لِهِ أَبَداً، وَسَلامُهُ دَائِماً سَرْمَداً', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'دعاء يوم الاثنين', '', 2, 'اللّٰهُمَّ اجْعَلْ أَوَّلَ يَوْمِى هٰذَا صَلاحاً، وَأَوْسَطَهُ فَلاحاً، وَآخِرَهُ نَجَاحاً', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'دعاء يوم الاثنين', '', 2, 'وَأَعُوذُ بِكَ مِنْ يَوْمٍ أَوَّلُهُ فَزَعٌ، وَأَوْسَطُهُ جَزَعٌ، وَآخِرُهُ وَجَعٌ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'دعاء يوم الاثنين', '', 2, 'اللّٰهُمَّ إِنِّى أَسْتَغْفِرُكَ لِكُلِّ نَذْرٍ نَذَرْتُهُ، وَكُلِّ وَعْدٍ وَعَدْتُهُ، وَكُلِّ عَهْدِ عَاهَدْتُهُ ثُمَّ لَمْ أَفِ بِهِ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'دعاء يوم الاثنين', '', 2, 'وَأَسْأَلُكَ فِى مَظَالِمِ عِبَادِكَ عِنْدِى، فَأَيُّما عَبْدٍ مِنْ عَبِيدِكَ أَوْ أَمَةٍ مِنْ إِمَائِكَ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'دعاء يوم الاثنين', '', 2, 'كَانَتْ لَهُ قِبَلِى مَظْلِمَةٌ ظَلَمْتُهَا إِيَّاهُ، فِى نَفْسِهِ، أَوْ فِى عِرْضِهِ،', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'دعاء يوم الاثنين', '', 2, 'أَوْ فِى مَالِهِ، أَوْ فِى أَهْلِهِ وَ وَلَدِهِ، أَوْ غِيبَةٌ اغْتَبْتُهُ بِهَا', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'دعاء يوم الاثنين', '', 2, ' أَوْ تَحَامُلٌ عَلَيْهِ بِمَيْلٍ أَوْ هَوَىً، أَوْ أَنَفَةٍ أَوْ حَمِيَّةٍ أَوْ رِيَاءٍ أَوْ عَصَبِيَّةٍ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'دعاء يوم الاثنين', '', 2, ' غَائِباً كَانَ أَوْ شَاهِداً، وَ حَيّاً كَانَ أَوْ مَيِّتاً', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'دعاء يوم الاثنين', '', 2, ' فَقَصُرَتْ يَدِى، وَضَاقَ وُسْعِى عَنْ رَدِّهَا إِلَيْهِ وَالتَّحَلُّلِ مِنْهُ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'دعاء يوم الاثنين', '', 2, 'فَأَسْأَلُكَ يَا مَنْ يَمْلِكُ الْحَاجَاتِ، وَهِىَ مُسْتَجِيبَةٌ لِمَشِيَّتِهِ، وَمُسْرِعَةٌ إِلَىٰ إِرادَتِهِ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'دعاء يوم الاثنين', '', 2, 'أَنْ تُصَلِّىَ عَلَىٰ مُحَمَّدٍ وَآلِ مُحَمَّدٍ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'دعاء يوم الاثنين', '', 2, 'وَأَنْ تُرْضِيَهُ عَنِّى بِمَا شِئْتَ، وَتَهَبَ لِى مِنْ عِنْدِكَ رَحْمَةً', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'دعاء يوم الاثنين', '', 2, ' إِنَّهُ لَاتَنْقُصُكَ الْمَغْفِرَةُ، وَلَا تَضُرُّكَ الْمَوْهِبَةُ، يَا أَرْحَمَ الرَّاحِمِينَ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'دعاء يوم الاثنين', '', 2, 'اللّٰهُمَّ أَوْلِنِى فِى كُلِّ يَوْمِ اثْنَيْنِ نِعْمَتَيْنِ مِنْكَ ثِنْتَيْنِ، سَعادَةً فِى أَوَّ لِهِ بِطَاعَتِكَ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'دعاء يوم الاثنين', '', 2, ' وَنِعْمَةً فِى آخِرِهِ بِمَغْفِرَتِكَ، يَا مَنْ هُوَ الْإِلٰهُ، وَلَا يَغْفِرُ الذُّنُوبَ سِوَاهُ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'دعاء يوم الثلاثاء', '', 2, 'بِسْمِ اللّهِ الرَّحْمَنِ الرَّحِيمِ، الْحَمْدُ لِلّٰهِ وَالْحَمْدُ حَقُّهُ كَمَا يَسْتَحِقُّهُ حَمْداً كَثِيراً', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'دعاء يوم الثلاثاء', '', 2, 'وَأَعُوذُ بِهِ مِنْ شَرِّ نَفْسِى إِنَّ النَّفْسَ لَأَمَّارَةٌ بِالسُّوءِ إِلّا مَا رَحِمَ رَبِّى', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'دعاء يوم الثلاثاء', '', 2, 'وَأَعُوذُ بِهِ مِنْ شَرِّ الشَّيْطَانِ الَّذِى يَزِيدُنِى ذَنْباً إِلَىٰ ذَنْبِى', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'دعاء يوم الثلاثاء', '', 2, ' وَأَحْتَرِزُ بِهِ مِنْ كُلِّ جَبَّارٍ فَاجِرٍ، وَسُلْطَانٍ جَائِرٍ، وَعَدُوٍّ قَاهِرٍ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'دعاء يوم الثلاثاء', '', 2, ' اللّٰهُمَّ اجْعَلْنِى مِنْ جُنْدِكَ فَإِنَّ جُنْدَكَ هُمُ الْغَالِبُونَ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'دعاء يوم الثلاثاء', '', 2, 'وَاجْعَلْنِى مِنْ حِزْبِكَ فَإِنَّ حِزْبَكَ هُمُ الْمُفْلِحُونَ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'دعاء يوم الثلاثاء', '', 2, 'وَاجْعَلْنِى مِنْ أَوْلِيَائِكَ فَإِنَّ أَوْلِياءَكَ لَاخَوْفٌ عَلَيْهِمْ وَلَا هُمْ يَحْزَنُونَ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'دعاء يوم الثلاثاء', '', 2, 'اللّٰهُمَّ أَصْلِحْ لِى دِينِى فَإِنَّهُ عِصْمَةُ أَمْرِى', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'دعاء يوم الثلاثاء', '', 2, ' وَأَصْلِحْ لِى آخِرَتِى فَإِنَّها دَارُ مَقَرِّى، وَ إِلَيْهَا مِنْ مُجاوَرَةِ اللِّئَامِ مَفَرِّى', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'دعاء يوم الثلاثاء', '', 2, ' وَاجْعَلِ الْحَيَاةَ زِيادَةً لِى فِى كُلِّ خَيْرٍ، وَالْوَفَاةَ رَاحَةً لِى مِنْ كُلِّ شَرٍّ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'دعاء يوم الثلاثاء', '', 2, 'اللّٰهُمَّ صَلِّ عَلَىٰ مُحَمَّدٍ خاتَمِ النَّبِيِّينَ، وَتَمَامِ عِدَّةِ الْمُرْسَلِينَ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'دعاء يوم الثلاثاء', '', 2, ' وَعَلَىٰ آلِهِ الطَّيِّبِينَ الطَّاهِرِينَ، وَأَصْحَابِهِ الْمُنْتَجَبِينَ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'دعاء يوم الثلاثاء', '', 2, ' وَهَبْ لِى فِى الثُّلَثَاءِ ثَلَاثاً: لَاتَدَعْ لِى ذنْباً إِلّا غَفَرْتَهُ، وَلَا غَمّاً إِلّا أَذْهَبْتَهُ، وَلَا عَدُوّاً إِلّا دَفَعْتَهُ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'دعاء يوم الثلاثاء', '', 2, ' بِبِسْمِ اللّٰهِ خَيْرِ الأَسْمَاءِ، بِسْمِ اللّٰهِ رَبِّ الْأَرْضِ وَالسَّماءِ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'دعاء يوم الثلاثاء', '', 2, 'أَسْتَدْفِعُ كُلَّ مَكْرُوهٍ أَوَّلُهُ سَخَطُهُ، وَأَسْتَجْلِبُ كُلَّ مَحْبُوبٍ أَوَّلُهُ رِضَاهُ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'دعاء يوم الثلاثاء', '', 2, ' فَاخْتِمْ لِى مِنْكَ بِالْغُفْرانِ يَا وَلِىَّ الْإِحْسَانِ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'دعاء يوم الاربعاء', '', 2, 'بِسْمِ اللّهِ الرَّحْمنِ الرَّحِيمِ، الْحَمْدُ لِلّٰهِ الَّذِى جَعَلَ اللَّيْلَ لِباساً، وَالنَّوْمَ سُباتاً، وَجَعَلَ النَّهارَ نُشُوراً', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'دعاء يوم الاربعاء', '', 2, 'لَكَ الْحَمْدُ أَنْ بَعَثْتَنِى مِنْ مَرْقَدِى وَلَوْ شِئْتَ جَعَلْتَهُ سَرْمَداً', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'دعاء يوم الاربعاء', '', 2, 'حَمْداً دائِماً لَايَنْقِطعُ أَبَداً، وَلَا يُحْصِى لَهُ الْخَلائِقُ عَدَداً', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'دعاء يوم الاربعاء', '', 2, 'اللّٰهُمَّ لَكَ الْحَمْدُ أَنْ خَلَقْتَ فَسَوَّيْتَ، وَقَدَّرْتَ وَقَضَيْتَ، وَ أَمَتَّ وَ أَحْيَيْتَ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'دعاء يوم الاربعاء', '', 2, 'وَأَمْرَضْتَ وَشَفَيْتَ، وَعافَيْتَ وَ أَبْلَيْتَ، وَعَلَى الْعَرْشِ اسْتَوَيْتَ، وَعَلَى الْمُلْكِ احْتَوَيْتَ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'دعاء يوم الاربعاء', '', 2, 'أَدْعُوكَ دُعَاءَ مَنْ ضَعُفَتْ وَسِيلَتُهُ، وَانْقَطَعَتْ حِيلَتُهُ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'دعاء يوم الاربعاء', '', 2, ' وَاقْتَرَبَ أَجَلُهُ، وَتَدَانَىٰ فِى الدُّنْيَا أَمَلُهُ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'دعاء يوم الاربعاء', '', 2, 'واشْتَدَّتْ إِلَىٰ رَحْمَتِكَ فاقَتُهُ، وَعَظُمَتْ لِتَفْرِيطِهِ حَسْرَتُهُ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'دعاء يوم الاربعاء', '', 2, 'وَكَثُرَتْ زَلَّتُهُ وَعَثْرَتُهُ، وَخَلُصَتْ لِوَجْهِكَ تَوْبَتُهُ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'دعاء يوم الاربعاء', '', 2, ' فَصَلِّ عَلَىٰ مُحَمَّدٍ خاتَمِ النَّبِيِّينَ، وَعَلَىٰ أَهْلِ بَيْتِهِ الطَّيِّبِينَ الطَّاهِرِينَ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'دعاء يوم الاربعاء', '', 2, 'وَارْزُقْنِى شَفاعَةَ مُحَمَّدٍ صَلَّى اللّٰهُ عَلَيْهِ وَآلِهِ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'دعاء يوم الاربعاء', '', 2, 'وَلَا تَحْرِمْنِى صُحْبَتَهُ، إِنَّكَ أَنْتَ أَرْحَمُ الرَّاحِمِينَ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'دعاء يوم الاربعاء', '', 2, ' اللّٰهُمَّ اقْضِ لِى فِى الْأَرْبَعاءِ أَرْبَعاً: اجْعَلْ قُوَّتِى فِى طَاعَتِكَ، وَنَشَاطِى فِى عِبَادَتِكَ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'دعاء يوم الاربعاء', '', 2, 'وَرَغْبَتِى فِى ثَوَابِكَ، وَزُهْدِى فِيَما يُوجِبُ لِى أَلِيمَ عِقَابِكَ، إِنَّكَ لَطِيفٌ لِمَا تَشَاءُ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'دعاء يوم الخميس', '', 2, 'بِسْمِ اللّهِ الرَّحْمنِ الرَّحِيمِ، الْحَمْدُ لِلّٰهِ الَّذِى أَذْهَبَ اللَّيْلَ مُظْلِماً بِقُدْرَتِهِ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'دعاء يوم الخميس', '', 2, 'وَجَاءَ بِالنَّهَارِ مُبْصِراً بِرَحْمَتِهِ، وَكَسَانِى ضِياءَهُ وَأَنَا فِى نِعْمَتِهِ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'دعاء يوم الخميس', '', 2, 'اللّٰهُمَّ فَكَمَا أَبْقَيْتَنِى لَهُ فَأَبْقِنِى لِأَمْثالِهِ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'دعاء يوم الخميس', '', 2, 'وَصَلِّ عَلَى النَّبِيِّ مُحَمَّدٍ وَآلِهِ،', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'دعاء يوم الخميس', '', 2, 'وَلَا تَفْجَعْنِي فِيهِ وَفِي غَيْرِهِ مِنَ اللَّيالِى وَالْأَيَّامِ، بِارْتِكَابِ الْمَحَارِمِ، وَاكْتِسَابِ الْمَآثِمِ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'دعاء يوم الخميس', '', 2, 'وَارْزُقْنِى خَيْرَهُ، وَخَيْرَ مَا فِيهِ، وَخَيْرَ مَا بَعْدَهُ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'دعاء يوم الخميس', '', 2, ' وَاصْرِفْ عَنِّى شَرَّهُ، وَشَرَّ مَا فِيهِ، وَشَرَّ مَا بَعْدَهُ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'دعاء يوم الخميس', '', 2, 'اللّٰهُمَّ إِنِّى بِذِمَّةِ الْإِسْلامِ أَتَوَسَّلُ إِلَيْكَ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'دعاء يوم الخميس', '', 2, 'وَبِحُرْمَةِ الْقُرْآنِ أَعْتَمِدُ عَلَيْكَ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'دعاء يوم الخميس', '', 2, 'وَبِمُحَمَّدٍ الْمُصْطَفىٰ صَلَّى اللّٰهُ عَلَيْهِ وَآلِهِ أَسْتَشْفِعُ لَدَيْكَ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'دعاء يوم الخميس', '', 2, 'فَاعْرِفِ اللّٰهُمَّ ذِمَّتِىَ الَّتِى رَجَوْتُ بِهَا قَضَاءَ حَاجَتِى، يَا أَرْحَمَ الرَّاحِمِينَ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'دعاء يوم الخميس', '', 2, 'اللّٰهُمَّ اقْضِ لِى‌فِى الْخَمِيسِ خَمْساً لَايَتَّسِعُ لَها إِلّا كَرَمُكَ، وَلَا يُطِيقُها إِلّا نِعَمُكَ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'دعاء يوم الخميس', '', 2, 'سَلَامَةً أَقْوىٰ بِهَا عَلَىٰ طَاعَتِكَ، وَعِبادَةً أَسْتَحِقُّ بِها جَزِيلَ مَثُوبَتِكَ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'دعاء يوم الخميس', '', 2, 'وَسَعَةً فِى الْحَالِ مِنَ الرِّزْقِ الْحَلَالِ، وَأَن تُؤْمِنَنِى فِى مَوَاقِفِ الْخَوْفِ بِأَمْنِكَ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'دعاء يوم الخميس', '', 2, 'وَتَجْعَلَنِى مِنْ طَوَارِقِ الْهُمُومِ وَالْغُمُومِ فِى حِصْنِكَ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'دعاء يوم الخميس', '', 2, 'وَ صَلِّ عَلَىٰ مُحَمَّدٍ وَآلِ مُحَمَّدٍ، وَاجْعَلْ تَوَسُّلِى بِهِ شَافِعاً، يَوْمَ الْقِيامَةِ نَافِعاً، إِنَّكَ أَنْتَ أَرْحَمُ الرَّاحِمِينَ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'دعاء يوم الجمعة', '', 2, 'بِسْمِ اللّهِ الرَّحْمنِ الرَّحِيمِ، الْحَمْدُ لِلّٰهِ الأَوَّلِ قَبْلَ الْإِنْشاءِ وَالْإِحْياءِ، وَالْآخِرِ بَعْدَ فَنَاءِ الأَشْياءِ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'دعاء يوم الجمعة', '', 2, 'الْعَلِيمِ الَّذِى لَايَنْسىٰ مَنْ ذَكَرَهُ، وَلَا يَنْقُصُ مَنْ شَكَرَهُ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'دعاء يوم الجمعة', '', 2, 'وَلَا يَخِيبُ مَنْ دَعَاهُ، وَلَا يَقْطَعُ رَجَاءَ مَنْ رَجَاهُ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'دعاء يوم الجمعة', '', 2, 'اللّٰهُمَّ إِنِّى أُشْهِدُكَ وَكَفىٰ بِكَ شَهِيداً، وَأُشْهِدُ جَمِيعَ مَلائِكَتِكَ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'دعاء يوم الجمعة', '', 2, 'وَسُكَّانَ سَمٰواتِكَ وَحَمَلَةِ عَرْشِكَ، وَمَنْ بَعَثْتَ مِنْ أَنْبِيَائِكَ وَ رُسُلِكَ، وَأَنْشَأْتَ مِنْ أَصْنَافِ خَلْقِكَ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'دعاء يوم الجمعة', '', 2, 'أَنِّى أَشْهَدُ أَنَّكَ أَنْتَ اللّٰهُ لَاإِلٰهَ إِلّا أَنْتَ وَحْدَكَ لَا شَرِيكَ لَكَ وَ لَا عَدِيلَ، وَلَا خُلْفَ لِقَوْلِكَ وَلَا تَبْدِيلَ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'دعاء يوم الجمعة', '', 2, 'وَأَنَّ مُحَمَّداً صَلَّى اللّٰهُ عَلَيْهِ وَآلِهِ عَبْدُكَ وَرَسُولُكَ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'دعاء يوم الجمعة', '', 2, 'أَدَّىٰ مَا حَمَّلْتَهُ إِلَى الْعِبَادِ، وَجَاهَدَ فِى اللّٰهِ عَزَّ وَجَلَّ حَقَّ الْجِهَادِ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'دعاء يوم الجمعة', '', 2, 'وَأَنَّهُ بَشَّرَ بِمَا هُوَ حَقٌّ مِنَ الثَّوَابِ، وَأَنْذَرَ بِمَا هُوَ صِدْقٌ مِنَ الْعِقَابِ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'دعاء يوم الجمعة', '', 2, 'اللّٰهُمَّ ثَبِّتْنِى عَلىٰ دِينِكَ مَا أَحْيَيْتَنِى وَلَا تُزِغْ قَلْبِى بَعْدَ إِذْ هَدَيْتَنِى', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'دعاء يوم الجمعة', '', 2, 'وَهَبْ لِى مِنْ لَدُنْكَ رَحْمَةً إِنَّكَ أَنْتَ الْوَهَّابُ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'دعاء يوم الجمعة', '', 2, 'صَلِّ عَلَىٰ مُحَمَّدٍ وَعَلَىٰ آلِ مُحَمَّدٍ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'دعاء يوم الجمعة', '', 2, 'وَاجْعَلْنِى مِنْ أَتْباعِهِ وَشِيعَتِهِ، وَاحْشُرْ نِى فِى زُمْرَتِهِ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'دعاء يوم الجمعة', '', 2, 'وَوَفِّقْنِى لِأَدَاءِ فَرْضِ الْجُمُعَاتِ، وَمَا أَوْجَبْتَ عَلَىَّ فِيها مِنَ الطَّاعَاتِ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'دعاء يوم الجمعة', '', 2, 'وَقَسَمْتَ لِأَهْلِهَا مِنَ الْعَطَاءِ فِى يَوْمِ الْجَزَاءِ، إِنَّكَ أَنْتَ الْعَزِيزُ الْحَكِيمُ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'في فضل ليلة الجمعة، ونهارها، وأعمالها', '', 1, 'اعلم أنّ ليلة الجمعة ونهارها يمتازان علىٰ سائر الليالي والأيَّام سموّاً وشرفاً ونباهةً.
روي عن النبيّ صلى‌الله‌عليه‌وآله‌وسلم قال: إنّ ليلة الجمعة ونهارها أربع وعشرون ساعة، لله عزوجل في كلّ ساعة 
ستّمئة ألف عتيق من النار .
وعن الصادق عليه‌السلام قال: من مات ما بين زوال الشمس من يوم الخميس الىٰ زوال الشمس من 
يوم الجمعة أعاذه الله من ضغطة القبر .
وعنه عليه‌السلام أيضاً قال: إن للجمعة حقّاً واجباً فإيّاك أن تضيّع حرمته أو تقصّر في شيء من 
عبادة الله تعالىٰ والتقرّب إليه بالعمل الصالح وترك المحارم كلّها، فإنّ الله تعالىٰ يضاعف فيه 
الحسنات، ويمحو السيّئات، ويرفع فيه الدرجات، ويومه مثل ليلته فإن استطعت أن تحييها 
بالدّعاء والصلاة فافعل، فإنّ الله تعالىٰ يرسل فيها الملائكة إلى السماء الدنيا لتضاعف فيها 
الحسنات، وتمحو فيها السيّئات، وإنَّ الله واسع كريم
وأيضاً في حديث معتبر عنه عليه‌السلام قال: إنّ المؤمن ليدعو في الحاجة فيؤخّر الله حاجته التي سأل 
إلىٰ يوم الجمعة ليخصّه بفضل يوم الجمعة (أي ليضاعف له بسبب فضل يوم الجمعة). وقال: لمّا 
سأل إخوة يوسف يعقوب أن يستغفر لهم قال: سوف أستغفر لكم ربي، ثمّ أخّر الاستغفار إلى 
السحر من ليلة الجمعة كي يستجاب له .
وعنه عليه‌السلام أيضاً قال: إذا كانت ليلة الجمعة رفعت حيتان البحور رؤوسها، ودوابّ البراري، 
ثمّ نادت بصوتٍ طلقٍ: رَبّنا لا تعذّبنا بذنوب الآدميّين .
وَعَن الباقِر عليه‌السلام قالَ : إنّ الله تعالى لَيأمر مَلَكا فَيُنادي كُلّ لَيلة جُمعة مِن فَوقِ عَرشِه مِن أوّل اللّيل إلى آخرهِ : إِلاّ عبد مُؤمِن يَدعوني لاخِرَتهِ وَدُنياه قَبلَ طُلوع الفَجر فَأجيبه ، ألا عبد مُؤمن يَتوب إلى مِن ذنوبهِ قَبلَ طُلوع الفَجر فَأتوب عَليه ، ألا عبد مُؤمن قَد قَترتُ عَليه رِزقه فيسألني الزّيادة في رِزقه قَبلَ طُلوع الفَجر فأزيده وَأوسع عَليه ، ألا عبد مُؤمن سقيم فَيسألني أنْ أشفيه قَبلَ طُلوع الفَجر فأعافيه ، أَلا عبد مُؤمن مَغموم مَحبوس يَسألني أنْ أطلقهُ مِن حَبسِه وَأفرج عَنهُ قَبلَ طُلوع الفَجر فأطلقهُ وَأخلّي سَبيلهُ ، ألا عبد مَظلوم يَسألني أنْ آخذ لَهُ بِظلامَته قَبلَ طُلوع الفَجر فأنتَصِر لَهُ وَآخذ بِظلامَتِهِ. قالَ : فلا يزال ينادي حتّى يَطلع الفَجر.
وَعَن أمير ألمُؤمنين عليه‌السلام قالَ : إنّ الله اختار الجُمعة فَجعل يَومها عيداً ، وَاختارَ لَيلتها فَجعلها مثلها. وَإنّ من فَضلِها أنْ لا يسأل الله عزّ وَجلّ أحد يَوم الجُمعة حاجة إلا استَجيب لَهُ ، وَإنِ استحق قَومٌ عِقاباً فَصادفوا يَوم الجُمعة وَليلتها صُرِفَ عَنهم ذلكَ وَلَمْ يبقَ شيٌ ممّا أحكَمه الله وفضّله إلا أبرمَه في ليلةِ الجُمعة. فَليلة الجُمعة أفضل اللّيالي وَيَومها أفضل الاَيّام.
وَعَن الصّادق عليه‌السلام قالَ : إجتَنِبوا المعاصي لَيلة الجُمعة ، فإنّ السّيّئة مُضاعَفة وَالحَسَنة مُضاعَفة ، وَمَنْ تَرك مَعصية الله لَيلة الجُمعة غَفَرَ الله لَهُ كُلّ ماسَلف ، وَمَنْ بَارزَ الله لَيلة الجُمعة بِمعصية أخذهُ الله بكلّ ما عمل في عُمرهِ وَضاعفَ عَليه العَذاب بِهذهِ المَعصية.
وَبِسَنَد مُعْتَبَر عَنْ الرّضا عليه‌السلام قالَ : قالَ رَسول الله صلى‌الله‌عليه‌وآله : إنّ يَوم الجُمعة سَيّد الاَيّام يُضاعِفُ الله عزّ وَجلّ فيهِ الحَسَنات وَيَمحو فيهِ السيئات وَيَرفع فيهِ الدّرجات وَيَستَجيب فيهِ الدّعوات وَيَكشِفُ فيهِ الكُربات وَيَقضي فيهِ الحوائِج العِظام ، وَهُوَ يَوم المزيد للهِ فيه عُتقاء وَطلقاء من النّار ما دعا فيه أحد من النّاس وَعرف حقّه وَحرمته إلاّ كانَ حقّا على الله عزّ وَجلّ أَن يجعله من عتقائِه وَطُلقائه من النّار ، فإنْ مات في يومه أو ليلته مات شهيداً وَبعث آمنا. وَما استخفّ أحد بحرمته وضيّع حقّه إلاّ كانَ حقاً على الله عَزّ وَجلّ أن يصليه نار جهنّم إلاّ أن يتوب.
وَبأسناد معتبرة عَن الباقِر عليه‌السلام قالَ : ما طلعت الشّمس بِيوم أفضل من يوم الجُمعة ، وَإنّ كلام الطّير إِذا لَقى بعضها بعضاً : سلام سلام يَوم صالح.
وَبسند معتبر عَن الصّادق عليه‌السلام قالَ : من وَافق منكم يَوم الجُمعة فلا يشتغلن بشي غَير العبادة فإنّ فيه يغفر للعباد وتنزل عَلَيهم الرّحمة. وَفضْل ليلة الجُمعة وَنهارها أكثر من أَن يورد في هذه الوجيزة.', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'في فضل ليلة الجمعة، ونهارها، وأعمالها', '', 1, 'امّا أعمال ليلة الجُمعة فكثيرة وهنا نقتصر على عدّة منها :
الأوّل: الإكثار من قول سُبْحَانَ اللهِ، وَاللهُ أَكْبَرُ، وَلَاٰ إِلٰهَ إِلَّا اللهُ. والإكثار من الصلاة علىٰ محمد وآله، فقد روي أنّ ليلة الجمعة ليلتها غرّاء، ويومها يوم زاهر، فأكثروا من قول سُبْحانَ اللهِ، وَاللهُ أَكْبَرُ، وَلاٰ إِلٰهَ إِلَّا اللهُ، وأكثروا من الصلاة علىٰ محمد وآل محمد عليهم‌السلام. وفي رواية اُخرىٰ أنّ أقلّ الصلاة علىٰ محمّد وآله في هذه الليلة مئة مرة وما زدت فهو أفضل.
وعن الصادق عليه‌السلام أنّ الصلاة علىٰ محمد وآله في ليلة الجمعة تعدل ألف حسنة، وتمحو ألف سيئة، وترفع ألف درجة ، ويستحبّ الاستكثار فيها من الصلاة علىٰ محمد وآل محمد صلوات الله عليهم من بعد صلاة العصر يوم الخميس إلىٰ آخر نهار الجمعة.
وروي بسند صحيح عن الصادق عليه‌السلام قال: إذا كان عصر الخميس نزل من السماء ملائكة بعدد الذرّ في أيديهم أقلام الذهب وقراطيس الفضّة لا يكتبون إلىٰ ليلة السبت إلَّا الصلاة علىٰ محمد وآل محمّد', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'في فضل ليلة الجمعة، ونهارها، وأعمالها', '', 1, 'وقال الشيخ الطوسي: ويستحب في يوم الخميس الصلاة علىٰ النبي صلى‌الله‌عليه‌وآله‌وسلم ألف مرّة، وَيستحبّ أن يقول فيه:', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'في فضل ليلة الجمعة، ونهارها، وأعمالها', '', 2, 'اللّٰهُمَّ صَلِّ عَلىٰ مُحَمَّدٍ وَآلِ مُحَمَّدٍ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'في فضل ليلة الجمعة، ونهارها، وأعمالها', '', 2, 'وَعَجِّلْ فَرَجَهُمْ، وَأَهْلِكْ عَدُوَّهُمْ مِنَ الْجِنِّ وَالْإِنْسِ مِنَ الأَوَّلِينَ وَالْآخِرِينَ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'في فضل ليلة الجمعة، ونهارها، وأعمالها', '', 1, 'وقال الشيخ أيضاً: ويستحبّ أن تستغفر آخر نهارك يوم الخميس فتقول:', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'في فضل ليلة الجمعة، ونهارها، وأعمالها', '', 2, 'أَسْتَغْفِرُ اللّٰهَ الَّذِى لا إِلٰهَ إِلّا هُوَ الْحَىُّ الْقَيُّومُ، وَأَتُوبُ إِلَيْهِ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'في فضل ليلة الجمعة، ونهارها، وأعمالها', '', 2, 'تَوْبَةَ عَبْدٍ خاضِعٍ مِسْكِينٍ مُسْتَكِينٍ، لَايَسْتَطِيعُ لِنَفْسِهِ صَرْفاً وَلَا عَدْلاً', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'في فضل ليلة الجمعة، ونهارها، وأعمالها', '', 2, 'وَلَا نَفْعاً وَلَا ضَرّاً وَلَا حَيَاةً وَلَا مَوْتاً وَلَا نُشُوراً', 'بهتر ست ردیف D حذف شود و در ردیف C قرار بگیرد'),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'في فضل ليلة الجمعة، ونهارها، وأعمالها', '', 2, ' وَصَلَّى اللّٰهُ عَلَىٰ مُحَمَّدٍ وَعِتْرَتِهِ الطَّيِّبِينَ الطَّاهِرِينَ الْأَخْيارِ الأَبْرارِ وَسَلَّمَ تَسْلِيماً', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'في فضل ليلة الجمعة، ونهارها، وأعمالها', '', 1, 'الثاني: أنْ يقرأ ليلة الجمعة سورة بني إسرائيل والكهف والسور الثلاث المبدوءة بطس وسورة الم السّجدة ويسۤ وص والأحقاف والواقعة وحم السجدة وحم الدّخان والطُّور واقتربت والجمعة، فإن لم تسنح له الفرصة فَلْيَخْتَرْ من هذه السور الواقعة وما قبلها، فقد روي عن الصادق عليه‌السلام قال: ما من عبد قرأ سورة بني إسرائيل في كلّ ليلة جمعة لم يمت حتىٰ يدرك القائم عليه‌السلام فيكون من أصحابه. وقال عليه‌السلام: من قرأ سورة الكهف كلّ ليلة جمعة لم يمت إلّا شهيداً، أو يبعثه الله مع الشهداء، ووقف يوم القيامة مع الشهداء. وقال عليه‌السلام: من قرأ الطواسين الثلاثة في ليلة الجمعة كان من أولياء الله، وفي جوار الله، وكنفه، ولم يُصبه في الدنيا بؤس أبداً، واُعطي في الآخرة من الجنّة حتّىٰ يرضىٰ وفوق رضاه، وزوّجه الله مئة زوجةٍ من الحور العين.
وقال عليه‌السلام: من قرأ سورة السجدة في كلّ ليلة جمعة أعطاه الله كتابه بيمينه، ولم يحاسبه بما كان منه، وكان من رفقاء محمّد وأهل بيته عليهم‌السلام.
وبسند معتبر عن الباقر عليه‌السلام قال: من قرأ سورة ص في ليلة الجمعة اُعطي من خير الدنيا والآخرة ما لم يُعط أحداٌ من الناس إلّا نبيٌّ مرْسلٌ أو ملكٌ مقرّبٌ، وأدخله الله الجنّة وكلّ من أحبّ من أهل بيته حتىٰ خادمه الذي يخدمه وإن لم يكن في حدّ عياله ولا في حدّ من يشفع له.
وعن الصّادق عليه‌السلام قال: من قرأ في ليلة الجمعة أو يوم الجمعة سورة الأحقاف لم يصبه الله بروعة في الحياة الدنيا، وآمنه من فزع يوم القيامة إن شاء الله. وقال عليه‌السلام: من قرأ الواقعة كلّ ليلة جمعة أحبّه الله تعالىٰ، وأحبّه إلىٰ الناس أجمعين، ولم يرَ في 
الدنيا بؤساً أبداً ولا فقراً ولا فاقة ولا آفةً من آفات الدّنيا، وكان من رفقاء أمير المؤمنين عليه‌السلام، وهذه السورة لأمير المؤمنين عليه‌السلام خاصة لا يشركه فيها أحد. وروي أنّ من قرأ سورة الجمعة كلّ ليلة جمعة كانت كفّارة له ما بين الجمعة إلىٰ الجمعة.
وروي مثله في مَن قرأ سورة الكهف في كلّ ليلة جمعة، وفي مَن قرأها بعد فريضتي الظّهر والعصر يوم الجمعة.
واعلم أنّ الصلوات المأثورة في ليلة الجمعة عديدةٌ منها: صلاة أمير المؤمنين عليه‌السلام.
ومنها الصلاة ركعتان يقرأ في كلّ ركعة الحمد وسورة إذا زُلزلت خمس عشرة مرّة، فقد روي أنّ من صلّاها آمنه الله تعالىٰ من عذاب القبر ومن أهوال يوم القيامة ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'سوره ی مغربین روز جمعه ', '', 1, 'الثالث: أن يقرأ سورة الجمعة في الركعة الأُولىٰ من فريضتي المغرب والعشاء، ويقرأ التوحيد في الثانية من المغرب، والأعلىٰ في الثانية من العشاء.', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', '', '', 1, 'الرابع: ترك إنشاد الشعر، ففي الصحيح عن الصادق صلوات الله وسلامه عليه أَنّه يكره رواية الشعر للصائم والمحرم وفي الحرم وفي يوم الجمعة وأن يروي بالليل، قال: وإن كان شعر حقّ؟ قال: وإن كان شعر حقّ؟ وفي حديث معتبر عن الصادق عليه‌السلام أنّ النبي صلى‌الله‌عليه‌وآله‌وسلم قال: من أنشد بيتاً من الشعر في ليلة الجمعة أو نهارها لم يكن له سواه نصيب من الثواب في تلك الليلة ونهارها.
وعلىٰ رواية أخرىٰ: لم تقبل صلاته في تلك الليلة ونهارها ', 'بهتر ست ردیف D حذف شود و در ردیف C قرار بگیرد'),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'دعا کردن در حق مومنان ', '', 1, 'الخامس: أن يكثر من الدعاء لإخوانه المؤمنين كما كانت تصنع الزهراء عليها‌السلام، وإذا دعا لعشر من الأموات منهم فقد وجبت له الجنّة كما في الحديث.', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'دعای سجده ی نافله عشا شب جمعه', '', 1, 'السادس: أن يدعو بالمأثور من أدعيتها وهي كثيرة، ونحن نقتصر علىٰ ذكر نبذٍ يسيرةٍ منها. بسند صحيح عن الصادق عليه‌السلام: أنّ من دعا بهذا الدعاء ليلة الجمعة في السجدة الأخيرة من نافلة العشاء سبع مرّات فرغ مغفوراً له، والأفضل أن يكرّر العمل في كلّ ليلة:', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', '', '', 2, 'اللّٰهُمَّ إِنِّى أَسْأَلُكَ بِوَجْهِكَ الْكَرِيمِ وَاسْمِكَ الْعَظِيمِ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', '', '', 2, ' أَنْ تُصَلِّىَ عَلىٰ مُحَمَّدٍ وَآلِ مُحَمَّدٍ، وَأَنْ تَغْفِرَ لِى ذَنْبِىَ الْعَظِيمَ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', '', '', 1, 'وعن النبيّ صلى‌الله‌عليه‌وآله‌وسلم قال: مَنْ قال هذه الكلمات سبع مرّات في ليلة الجمعة فمات ليلته دخل الجنّة،
ومن قالها يوم الجمعة فمات في ذلك اليوم دخل الجنّة:', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', '', '', 2, 'اللّٰهُمَّ أَنْتَ رَبِّى لَاإِلٰهَ إِلّا أَنْتَ خَلَقْتَنِى', 'لینک'),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', '', '', 2, 'وَأَنَا عَبْدُكَ وَابْنُ أَمَتِكَ، وَفِى قَبْضَتِكَ وَناصِيَتِى بِيَدِكَ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', '', '', 2, 'أَمْسَيْتُ عَلَىٰ عَهْدِكَ وَ وَعْدِكَ مَا اسْتَطَعْتُ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', '', '', 2, 'أَعُوذُ بِرِضاكَ مِنْ شَرِّ مَا صَنَعْتُ، أَبُوءُ بِنِعْمَتِكَ [بِعَمَلِى]، وَأَبُوءُ بِذَنْبِى [بِذُنوُبِى]', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', '', '', 2, 'فَاغْفِرْ لِى ذُنُوبِى إِنَّهُ لَايَغْفِرُ الذُّنُوبَ إِلّا أَنْتَ', 'بهتر ست ردیف D حذف شود و در ردیف C قرار بگیرد'),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', '', '', 1, 'وقال الشيخ الطوسي والسيّد والكفعمي والسيّد ابن باقي: يستحب أن يدعىٰ بهذا الدعاء في ليلة الجمعة ونهارها وفي ليلة عرفة ونهارها، ونحن نروي الدعاء عن كتاب المصباح للشيخ وهو:', 'بهتر ست ردیف D حذف شود و در ردیف C قرار بگیرد'),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', '', '', 2, 'اللّٰهُمَّ مَنْ تَعَبَّأَ وَ تَهَيَّأَ وَأَعَدَّ وَاسْتَعَدَّ لِوِفَادَةٍ إِلَىٰ مَخْلُوقٍ رَجَاءَ رِفْدِهِ وَ طَلَبَ نائِلِهِ وَجائِزَتِهِ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', '', '', 2, ' فَإِلَيْكَ يَا رَبِّ تَعْبِيَتِى وَاسْتِعْدادِى رَجَاءَ عَفْوِكَ وَطَلَبَ نائِلِكَ وَجَائِزَتِكَ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', '', '', 2, ' فَلَا تُخَيِّبْ دُعَائِى يَا مَنْ لَايَخِيبُ عَلَيْهِ سائِلٌ وَلَا يَنْقُصُهُ نائِلٌ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', '', '', 2, 'فَإِنِّى لَمْ آتِكَ ثِقَةً بِعَمَلٍ صَالِحٍ عَمِلْتُهُ، وَلَا لِوَفادَةِ مَخْلُوقٍ رَجَوْتُهُ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', '', '', 2, 'أَتَيْتُكَ مُقِرّاً عَلَىٰ نَفْسِى بِالْإِساءَةِ وَالظُّلْمِ، مُعْتَرِفاً بِأَنْ لَاحُجَّةَ لِى وَلَا عُذْرَ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', '', '', 2, 'أَتَيْتُكَ أَرْجُو عَظِيمَ عَفْوِكَ الَّذِى عَفَوْتَ بِهِ عَنِ الْخاطِئِينَ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', '', '', 2, 'فَلَمْ يَمْنَعْكَ طُولُ عُكُوفِهِمْ عَلىٰ عَظِيمِ الْجُرْمِ أَنْ عُدْتَ عَلَيْهِمْ بِالرَّحْمَةِ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', '', '', 2, ' فَيَا مَنْ رَحْمَتُهُ واسِعَةٌ، وَ عَفْوُهُ عَظِيمٌ، يَا عَظِيمُ يَا عَظِيمُ يَا عَظِيمُ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', '', '', 2, ' لَايَرُدُّ غَضَبَكَ إِلّا حِلْمُكَ، وَلَا يُنْجِى مِنْ سَخَطِكَ إِلّا التَّضَرُّعُ إِلَيْكَ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', '', '', 2, 'فَهَبْ لِى يَا إِلٰهِى فَرَجاً بِالْقُدْرَةِ الَّتِى تُحْيِى بِهَا مَيْتَ الْبِلَادِ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', '', '', 2, 'وَلَا تُهْلِكْنِى غَمّاً حَتَّىٰ تَستَجِيبَ لِى، وَتُعَرِّفَنِى الْإِجابَةَ فِى دُعَائِى', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', '', '', 2, 'وَأَذِقْنِى طَعْمَ الْعَافِيَةِ إِلَىٰ مُنْتَهىٰ أَجَلِى، وَلَا تُشْمِتْ بِى عَدُوِّى، وَلَا تُسَلِّطْهُ عَلَىَّ، وَلَا تُمَكِّنْهُ مِنْ عُنُقِى', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', '', '', 2, ' اللّٰهُمَّ إِنْ وَضَعْتَنِى فَمَنْ ذَا الَّذِى يَرْفَعُنِى وَ إِنْ رَفَعْتَنِى فَمَنْ ذَا الَّذِى يَضَعُنِى', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', '', '', 2, 'وَ إِنْ أَهْلَكْتَنِى فَمَنْ ذَا الَّذِى يَعْرِضُ لَكَ فِى عَبْدِكَ أَوْ يَسْأَلُكَ عَنْ أَمْرِهِ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', '', '', 2, 'وَقَدْ عَلِمْتُ أَنَّهُ لَيْسَ فِى حُكْمِكَ ظُلْمٌ، وَلَا فِى نَقِمَتِكَ عَجَلَةٌ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', '', '', 2, ' وَ إِنَّمَا يَعْجَلُ مَنْ يَخَافُ الْفَوْتَ، وَ إِنَّمَا يَحْتاجُ إِلَى الظُّلْمِ الضَّعِيفُ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', '', '', 2, 'وَقَدْ تَعالَيْتَ يَا إِلٰهِى عَنْ ذٰلِكَ عُلُوّاً كَبِيراً', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', '', '', 2, 'اللّٰهُمَّ إِنِّى أَعُوذُ بِكَ فَأَعِذْنِى، وَأَسْتَجِيرُ بِكَ فَأَجِرْنِى', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', '', '', 2, ' وَأَسْتَرْزِقُكَ فَارْزُقْنِى، وَأَتَوَكَّلُ عَلَيْكَ فَاكْفِنِى', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', '', '', 2, ' وَأَسْتَنْصِرُكَ عَلىٰ عَدُوِّى فَانْصُرْنِى، وَأَسْتَعِينُ بِكَ فَأَعِنِّى', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', '', '', 2, 'وَأَسْتَغْفِرُكَ يَا إِلٰهِى فَاغْفِرْ لِى، آمِينَ آمِينَ آمِينَ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', '', '', 1, 'السابع: أن يدعو بدعاء كميل، وسيذكر في الفصل الآتي إن شاء الله تعالى ص 115.', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'دعای اللّٰهُمَّ يَا شاهِدَ كُلِّ نَجْوىٰ ', '', 1, 'الثامن: أن يقرأ الدعاء: اللّٰهُمَّ يَا شَاهِدَ كُلِّ نَجْوىٰ. ويدعىٰ به ليلة عرفة أيضاً، وسيأتي إن شاء الله تعالىٰ ص 336.', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', 'دعای یا دائم الفضل علی البریه', '', 1, 'التاسع: أن يقول عشر مرّات: ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', '', '', 2, 'يَا دائِمَ الْفَضْلِ عَلَى الْبَرِيَّةِ، يَا بَاسِطَ الْيَدَيْنِ بِالْعَطِيَّةِ، يَا صَاحِبَ الْمَوَاهِبِ السَّنِيَّةِ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', '', '', 2, 'صَلِّ عَلَىٰ مُحَمَّدٍ وَآلِهِ خَيْرِ الْوَرَىٰ سَجِيَّةً، وَاغْفِرْ لَنا يَا ذَا الْعُلىٰ فِي هَذِهِ الْعَشِيَّةِ', ''),
    ('الباب الاوّل في تعقيب الصلوات ودعوات أيّام', 'الفصل الثالث : في دعوات أيام الأسبوع', '', '', 1, 'وهذا الذكر الشريف وارد في ليلة عيد الفطر أيضاً.', '');
